# 1. Crear archivo completamente nuevo con timestamp √∫nico
 cd /var/www/moonyetis-slots/frontend
 TIMESTAMP=$(date +%s)

 # 2. Crear nueva versi√≥n desde cero con wallet funcional
 cat > index-wallet-fixed-$TIMESTAMP.html << 'EOF'
 <!DOCTYPE html>
 <html lang="en">
 <head>
     <meta charset="UTF-8">
     <meta name="viewport" content="width=device-width, initial-scale=1.0">
     <title>MoonYetis Slots - Fractal Bitcoin Casino</title>
     <style>
         body { font-family: Arial, sans-serif; margin: 0; padding: 20px;
 background: #1a1a2e; color: white; }
         .container { max-width: 800px; margin: 0 auto; text-align: center; }
         .wallet-btn { background: #00ff88; color: black; padding: 15px 30px;
  border: none; border-radius: 10px; font-size: 18px; cursor: pointer; 
 margin: 10px; }
         .wallet-btn:hover { background: #00cc66; }
         .status { margin: 20px 0; padding: 15px; background: #333; 
 border-radius: 10px; }
         .slots-area { margin: 30px 0; padding: 20px; background: #2a2a4e; 
 border-radius: 15px; }
     </style>
 </head>
 <body>
     <div class="container">
         <h1>üé∞ MoonYetis Slots</h1>
         <h2>Fractal Bitcoin Casino</h2>

         <div class="status">
             <div id="wallet-status">No wallet connected</div>
             <button class="wallet-btn" onclick="connectWallet()">Connect
 Wallet</button>
         </div>

         <div class="slots-area">
             <h3>üéÆ Casino Game Area</h3>
             <p>Connect your wallet to start playing with MOONYETIS
 tokens!</p>
             <div id="game-area" style="display:none;">
                 <p>Game will appear here after wallet connection</p>
             </div>
         </div>
     </div>

     <script>
         console.log('üé∞ MoonYetis Slots Loading...');

         async function connectWallet() {
             console.log('üöÄ WALLET CONNECTION: Starting...');
             document.getElementById('wallet-status').innerHTML =
 'Connecting...';

             // Try UniSat first
             if (window.unisat) {
                 try {
                     console.log('ü¶Ñ Trying UniSat...');
                     const accounts = await window.unisat.requestAccounts();
                     console.log('‚úÖ UniSat SUCCESS:', accounts);

                     document.getElementById('wallet-status').innerHTML =
                         '‚úÖ UniSat Connected: ' + accounts[0].substring(0,
 16) + '...';
                     document.getElementById('game-area').style.display =
 'block';

                     alert('üéâ UniSat Wallet Connected!\n' + accounts[0]);
                     return {provider: 'unisat', address: accounts[0]};
                 } catch(error) {
                     console.log('‚ùå UniSat error:', error);
                 }
             }

             // Try OKX
             if (window.okxwallet && window.okxwallet.bitcoin) {
                 try {
                     console.log('üü† Trying OKX...');
                     const accounts = await
 window.okxwallet.bitcoin.requestAccounts();
                     console.log('‚úÖ OKX SUCCESS:', accounts);

                     document.getElementById('wallet-status').innerHTML =
                         '‚úÖ OKX Connected: ' + accounts[0].substring(0, 16)
 + '...';
                     document.getElementById('game-area').style.display =
 'block';

                     alert('üéâ OKX Wallet Connected!\n' + accounts[0]);
                     return {provider: 'okx', address: accounts[0]};
                 } catch(error) {
                     console.log('‚ùå OKX error:', error);
                 }
             }

             // No wallets found
             console.log('‚ùå No wallets could connect');
             document.getElementById('wallet-status').innerHTML =
                 '‚ùå No wallets found. Install UniSat or OKX wallet.';
             alert('‚ùå No wallets detected!\n\nPlease install:\n‚Ä¢ UniSat 
 Wallet: https://unisat.io\n‚Ä¢ OKX Wallet: https://okx.com/web3');
         }

         // Test wallet availability on load
         setTimeout(() => {
             console.log('üîç Checking wallets...');
             console.log('UniSat:', typeof window.unisat, !!window.unisat);
             console.log('OKX:', typeof window.okxwallet,
 !!window.okxwallet);
         }, 2000);
     </script>
 </body>
 </html>
 EOF

 # 3. Configurar Nginx para servir la nueva versi√≥n
 sudo tee /etc/nginx/sites-available/moonyetis-new << EOF
 server {
     listen 443 ssl http2;
     server_name moonyetis.io;

     ssl_certificate /etc/letsencrypt/live/moonyetis.io/fullchain.pem;
     ssl_certificate_key /etc/letsencrypt/live/moonyetis.io/privkey.pem;

     root /var/www/moonyetis-slots/frontend;
     index index-wallet-fixed-$TIMESTAMP.html;

     location / {
         try_files \$uri \$uri/ /index-wallet-fixed-$TIMESTAMP.html;
         add_header Cache-Control "no-cache, no-store, must-revalidate";
     }
 }
 EOF

 # 4. Activar nueva configuraci√≥n
 sudo ln -sf /etc/nginx/sites-available/moonyetis-new
 /etc/nginx/sites-enabled/moonyetis
 sudo nginx -t && sudo systemctl reload nginx

 # 5. Verificar nueva versi√≥n
 curl -s https://moonyetis.io/ | grep "WALLET CONNECTION"
 echo "Nueva versi√≥n creada: index-wallet-fixed-$TIMESTAMP.html"

