79:            background: 
80:                radial-gradient(circle at 20% 10%, rgba(255, 107, 53, 0.15) 0%, transparent 50%),
81:                radial-gradient(circle at 80% 90%, rgba(78, 205, 196, 0.1) 0%, transparent 50%),
