// Wallet Integration - Clean Refactor v4
console.log('ğŸŸ¢ Wallet Integration Refactor v4 Loaded');

// Main wallet connection function
async function connectWallet() {
    try {
        // Detect wallets
        const availableWallets = await myWalletManager.detectAvailableWallets();
        if (!availableWallets.unisat.available && !availableWallets.okx.available) {
            showWalletInstallationMessage(availableWallets);
            return;
        }
        // Show modal and get user choice
        const walletChoice = await showWalletModalClean(availableWallets);
        if (!walletChoice) {
            slotMachine.showMessage('âš ï¸ ConexiÃ³n cancelada', 'lose');
            return;
        }
        slotMachine.showMessage('ğŸ”„ Conectando wallet...', 'neutral');
        // Connect to selected wallet
        let connectionResult;
        if (walletChoice === 'unisat') {
            connectionResult = await myWalletManager.connectUniSat();
        } else if (walletChoice === 'okx') {
            connectionResult = await myWalletManager.connectOKX();
        }
        if (connectionResult.success) {
            const backendVerification = await verifyWalletWithBackend(connectionResult);
            if (backendVerification.success) {
                gameState.connectedWallet = true;
                gameState.walletAddress = connectionResult.address;
                gameState.walletType = connectionResult.walletType;
                gameState.balance = connectionResult.balance.total;
                updateWalletUI(connectionResult);
                slotMachine.updateUI();
                setupWalletEventListeners();
                await registerForDepositMonitoring();
                slotMachine.showMessage(`ğŸ‰ Wallet ${connectionResult.walletType.toUpperCase()} conectada!`, 'win');
            } else {
                slotMachine.showMessage(`âŒ Error verificando wallet: ${backendVerification.error}`, 'lose');
            }
        } else {
            slotMachine.showMessage(`âŒ Error conectando: ${connectionResult.error}`, 'lose');
        }
    } catch (error) {
        console.error('âŒ Error en conexiÃ³n de wallet:', error);
        slotMachine.showMessage(`âŒ Error inesperado: ${error.message}`, 'lose');
    }
}

// Clean modal for wallet selection
async function showWalletModalClean(availableWallets) {
    return new Promise((resolve) => {
        // Remove any existing modals
        document.querySelectorAll('.wallet-selector-modal').forEach(m => m.remove());
        // Create modal
        const modal = document.createElement('div');
        modal.className = 'wallet-selector-modal';
        Object.assign(modal.style, {
            zIndex: '99999', position: 'fixed', top: 0, left: 0, width: '100vw', height: '100vh',
            display: 'flex', alignItems: 'center', justifyContent: 'center', background: 'rgba(10,14,26,0.85)'
        });
        modal.innerHTML = `
            <div style="background: var(--gradient-glass); border-radius: 24px; padding: 2rem; max-width: 400px; width: 90%; text-align: center; box-shadow: 0 8px 32px rgba(0,0,0,0.25);">
                <h3>ğŸ”— Conecta tu Wallet</h3>
                <p>Selecciona tu wallet preferida para jugar MoonYetis Slots</p>
                <div style="display: flex; flex-direction: column; gap: 1rem; margin: 1.5rem 0;">
                    ${availableWallets.unisat.available ?
                        `<button data-wallet="unisat" style="padding: 1rem; font-size: 1.1rem; border-radius: 12px; font-weight: 600; cursor: pointer; background: var(--gradient-primary); color: var(--dark); border: none;">ğŸ¦„ UniSat <span style='margin-left:0.5rem;color:#4ECD64;'>âœ… Detectada</span></button>` :
                        `<button data-wallet="unisat" data-action="install" style="padding: 1rem; font-size: 1.1rem; border-radius: 12px; font-weight: 600; cursor: not-allowed; background: #eee; color: #aaa; border: none;">ğŸ¦„ UniSat <span style='margin-left:0.5rem;color:#E55A2B;'>âŒ No instalada</span></button>`
                    }
                    ${availableWallets.okx.available ?
                        `<button data-wallet="okx" style="padding: 1rem; font-size: 1.1rem; border-radius: 12px; font-weight: 600; cursor: pointer; background: var(--gradient-primary); color: var(--dark); border: none;">ğŸŸ  OKX <span style='margin-left:0.5rem;color:#4ECD64;'>âœ… Detectada</span></button>` :
                        `<button data-wallet="okx" data-action="install" style="padding: 1rem; font-size: 1.1rem; border-radius: 12px; font-weight: 600; cursor: not-allowed; background: #eee; color: #aaa; border: none;">ğŸŸ  OKX <span style='margin-left:0.5rem;color:#E55A2B;'>âŒ No instalada</span></button>`
                    }
                </div>
                <button data-action="cancel" style="margin-top: 1rem; padding: 0.75rem 1.5rem; border-radius: 12px; font-weight: 600; cursor: pointer; background: #fff; color: var(--dark); border: 1px solid #eee;">Cancelar</button>
            </div>
        `;
        // Click handler
        modal.addEventListener('click', (event) => {
            const button = event.target.closest('button');
            if (!button) return;
            const walletType = button.dataset.wallet;
            const action = button.dataset.action;
            if (action === 'install') {
                const urls = { unisat: 'https://unisat.io/', okx: 'https://okx.com/web3' };
                window.open(urls[walletType], '_blank');
                return;
            }
            if (action === 'cancel') {
                modal.remove();
                resolve(null);
                return;
            }
            if (walletType && !button.hasAttribute('data-action')) {
                modal.remove();
                resolve(walletType);
            }
        });
        document.body.appendChild(modal);
    });
}

// Verificar wallet con backend
async function verifyWalletWithBackend(connectionData) {
    try {
        console.log('ğŸ”„ Verificando wallet con backend...');
        console.log('ğŸ“¤ Datos de conexiÃ³n:', connectionData);

        slotMachine.showMessage('ğŸ” Verificando firma con servidor...', 'neutral');

        const payload = {
            address: connectionData.address,
            signature: connectionData.signature.signature,
            message: connectionData.signature.message,
            walletType: connectionData.walletType,
            timestamp: connectionData.signature.timestamp
        };

        console.log('ğŸ“¤ Payload enviado:', payload);

        // Check if we're in development mode (no backend running)
        const isDevelopment = connectionData.signature === 'fake_signature_for_development_testing_only' || 
                             connectionData.signature === 'fake_okx_signature_for_development_testing_only';

        if (isDevelopment) {
            console.log('ğŸš§ MODO DESARROLLO: Simulando verificaciÃ³n exitosa del backend');
            return {
                success: true,
                message: 'VerificaciÃ³n simulada para desarrollo',
                address: connectionData.address,
                walletType: connectionData.walletType,
                developmentMode: true
            };
        }

        const response = await fetch('/api/wallet/connect', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(payload)
        });

        console.log('ğŸ“¥ Response status:', response.status);

        const result = await response.json();
        console.log('ğŸ“¥ Response data:', result);

        if (!response.ok) {
            throw new Error(result.error?.message || result.error || `HTTP ${response.status}`);
        }

        return result;
    } catch (error) {
        console.error('âŒ Error verificando con backend:', error);

        // Proporcionar errores mÃ¡s especÃ­ficos
        let errorMessage = error.message;
        if (error.message.includes('firma invÃ¡lida')) {
            errorMessage = 'La firma no es vÃ¡lida. Intenta conectar de nuevo.';
        } else if (error.message.includes('expirado')) {
            errorMessage = 'El mensaje de firma expirÃ³. Intenta de nuevo.';
        } else if (error.message.includes('network')) {
            errorMessage = 'Error de conexiÃ³n con el servidor. Verifica tu internet.';
        }

        return { success: false, error: errorMessage };
    }
}

// Actualizar UI de wallet
function updateWalletUI(connectionData) {
    // Actualizar botÃ³n de wallet
    const walletBtn = document.getElementById('connectWallet');
    const walletBtnText = document.getElementById('walletBtnText');
    if (walletBtn && walletBtnText) {
        walletBtnText.textContent = `${connectionData.walletType === 'unisat' ? 'ğŸ¦„' : 'ğŸŸ '} ${connectionData.address.substring(0, 6)}...${connectionData.address.substring(-4)}`;
        walletBtn.onclick = disconnectWallet;
        walletBtn.title = 'Click to disconnect wallet';
    }

    // Actualizar status de conexiÃ³n
    const connectionStatus = document.getElementById('connectionStatus');
    const statusText = document.getElementById('statusText');
    if (connectionStatus && statusText) {
        connectionStatus.className = 'status-indicator connected';
        statusText.textContent = `Connected (${connectionData.walletType.toUpperCase()})`;
    }

    // Actualizar balance
    const balanceDisplay = document.getElementById('balanceDisplay');
    const balance = document.getElementById('balance');
    if (balanceDisplay && balance) {
        balanceDisplay.style.display = 'block';
        balance.textContent = connectionData.balance.total;
    }
}

// Setup event listeners para wallet
function setupWalletEventListeners() {
    myWalletManager.onAccountChanged = (newAccount) => {
        slotMachine.showMessage('ğŸ”„ Cuenta de wallet cambiada, reconectando...', 'neutral');
        setTimeout(() => {
            disconnectWallet();
        }, 2000);
    };

    myWalletManager.onNetworkChanged = (network) => {
        console.log('ğŸŒ Red cambiada:', network);
        slotMachine.showMessage('âš ï¸ Red de wallet cambiada', 'neutral');
    };
}

// Desconectar wallet
function disconnectWallet() {
    console.log('ğŸ”Œ Desconectando wallet...');

    myWalletManager.disconnect();

    gameState.connectedWallet = false;
    gameState.walletAddress = null;
    gameState.walletType = null;
    gameState.balance = 0;

    // Restaurar botÃ³n de wallet
    const walletBtn = document.getElementById('connectWallet');
    const walletBtnText = document.getElementById('walletBtnText');
    if (walletBtn && walletBtnText) {
        walletBtnText.textContent = 'Connect Wallet';
        walletBtn.onclick = connectWallet;
        walletBtn.title = 'Connect your wallet to start playing';
    }

    // Actualizar status de conexiÃ³n
    const connectionStatus = document.getElementById('connectionStatus');
    const statusText = document.getElementById('statusText');
    if (connectionStatus && statusText) {
        connectionStatus.className = 'status-indicator disconnected';
        statusText.textContent = 'Not Connected';
    }

    // Ocultar balance
    const balanceDisplay = document.getElementById('balanceDisplay');
    if (balanceDisplay) {
        balanceDisplay.style.display = 'none';
    }

    slotMachine.updateUI();
    slotMachine.showMessage('ğŸ”Œ Wallet desconectada', 'neutral');
}

// Mostrar mensaje de instalaciÃ³n de wallets
function showWalletInstallationMessage(availableWallets) {
    const message = `
        âš ï¸ No se encontraron wallets compatibles.

        Instala una de estas wallets:
        ${!availableWallets.unisat.installed ? 'â€¢ UniSat Wallet (https://unisat.io/)' : ''}
        ${!availableWallets.okx.installed ? 'â€¢ OKX Wallet (https://okx.com/web3)' : ''}
    `;

    slotMachine.showMessage(message, 'lose');
}

// Registrar para monitoreo de depÃ³sitos
async function registerForDepositMonitoring() {
    try {
        console.log('ğŸ“ Registrando para monitoreo de depÃ³sitos...');

        const response = await fetch('/api/transactions/monitor/register', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            }
        });

        const result = await response.json();

        if (result.success) {
            console.log('âœ… Registrado para monitoreo de depÃ³sitos');

            // Iniciar verificaciÃ³n periÃ³dica de depÃ³sitos
            startDepositCheck();
        } else {
            console.warn('âš ï¸ Error registrando para monitoreo:', result.error);
        }

    } catch (error) {
        console.error('âŒ Error en registro de monitoreo:', error);
    }
}

// Verificar depÃ³sitos pendientes periÃ³dicamente
function startDepositCheck() {
    // Verificar depÃ³sitos cada 30 segundos
    setInterval(async () => {
        await checkPendingDeposits();
    }, 30000);

    // Verificar inmediatamente
    checkPendingDeposits();
}

// Verificar depÃ³sitos pendientes
async function checkPendingDeposits() {
    try {
        const response = await fetch('/api/transactions/deposits/pending');
        const result = await response.json();

        if (result.success && result.pendingDeposits.length > 0) {
            console.log(`ğŸ’° ${result.pendingDeposits.length} depÃ³sitos pendientes encontrados`);

            // Mostrar notificaciÃ³n si hay depÃ³sitos nuevos
            result.pendingDeposits.forEach(deposit => {
                if (!deposit.notified) {
                    slotMachine.showMessage(`ğŸ’° DepÃ³sito detectado: ${deposit.amount} sats (${deposit.confirmations} confirmaciones)`, 'neutral');
                    deposit.notified = true;
                }
            });
        }
    } catch (error) {
        console.error('âŒ Error verificando depÃ³sitos:', error);
    }
}

// Refrescar balance y verificar depÃ³sitos
async function refreshBalanceAndDeposits() {
    try {
        slotMachine.showMessage('ğŸ”„ Actualizando balance...', 'neutral');

        const response = await fetch('/api/transactions/balance/refresh');
        const result = await response.json();

        if (result.success) {
            // Actualizar balance local
            gameState.balance = result.balance.total;
            slotMachine.updateUI();

            if (result.pendingDeposits.length > 0) {
                slotMachine.showMessage(`ğŸ’° Balance actualizado. ${result.pendingDeposits.length} depÃ³sitos pendientes.`, 'win');
            } else {
                slotMachine.showMessage('âœ… Balance actualizado', 'win');
            }
        } else {
            slotMachine.showMessage(`âŒ Error actualizando: ${result.error}`, 'lose');
        }

    } catch (error) {
        console.error('âŒ Error refrescando balance:', error);
        slotMachine.showMessage('âŒ Error de conexiÃ³n', 'lose');
    }
}

// Additional utility functions for UI feedback
function showSuccessMessage(message) {
    slotMachine.showMessage(`âœ… ${message}`, 'win');
}

function showErrorMessage(message) {
    slotMachine.showMessage(`âŒ ${message}`, 'lose');
}

function showNotification(message, type = 'neutral') {
    slotMachine.showMessage(message, type);
}

// Wallet modal functionality (if needed)
function showWalletModal() {
    // This is handled by connectWallet() function
    connectWallet();
}

function closeWalletModal() {
    // Close any existing wallet modals
    const existingModals = document.querySelectorAll('.wallet-selector-modal');
    existingModals.forEach(modal => {
        if (modal.parentNode) {
            modal.parentNode.removeChild(modal);
        }
    });
}

// Initialize wallet connection setup when DOM is ready
document.addEventListener('DOMContentLoaded', function() {
    console.log('ğŸ”— Wallet Integration DOM Ready');
    console.log('ğŸ” Initial globals check:', {
        gameState: typeof gameState !== 'undefined',
        myWalletManager: typeof myWalletManager !== 'undefined',
        slotMachine: typeof slotMachine !== 'undefined'
    });

    // Ensure button exists and setup click handler
    const connectBtn = document.getElementById('connectWallet');
    console.log('ğŸ”˜ Button search result:', connectBtn ? 'Found' : 'Not found');

    if (connectBtn) {
        // Remove any existing listeners to avoid duplicates
        const newBtn = connectBtn.cloneNode(true);
        connectBtn.parentNode.replaceChild(newBtn, connectBtn);

        // Add our consolidated event listener
        newBtn.addEventListener('click', function() {
            console.log('ğŸ”— Connect button clicked');
            console.log('ğŸ” Available globals:', {
                gameState: typeof gameState !== 'undefined',
                myWalletManager: typeof myWalletManager !== 'undefined',
                connectWallet: typeof connectWallet === 'function'
            });

            // Verificar que gameState estÃ© disponible
            if (typeof gameState !== 'undefined') {
                console.log('ğŸ® gameState.connectedWallet:', gameState.connectedWallet);

                if (gameState.connectedWallet) {
                    disconnectWallet();
                } else {
                    connectWallet();
                }
            } else {
                console.error('âŒ gameState no estÃ¡ disponible');
                // Intentar conectar directamente si gameState no estÃ¡ disponible
                connectWallet();
            }
        });

        console.log('âœ… Connect wallet button setup completed');
    } else {
        console.warn('âš ï¸ Connect wallet button not found');
    }
});

// Expose functions globally
window.connectWallet = connectWallet;
window.disconnectWallet = disconnectWallet;
window.refreshBalanceAndDeposits = refreshBalanceAndDeposits;
window.showSuccessMessage = showSuccessMessage;
window.showErrorMessage = showErrorMessage;
window.showNotification = showNotification;
window.showWalletModal = showWalletModal;
window.closeWalletModal = closeWalletModal;

console.log('âœ… Wallet Integration Bridge Loaded [v3.0 - FIXED VERSION - Backend Simulation Applied]');

