                        `<button data-wallet="unisat" style="padding: 1rem; font-size: 1.1rem; border-radius: 12px; font-weight: 600; cursor: pointer; background: var(--gradient-primary); color: var(--dark); border: none;">ğŸ¦„ UniSat <span style='margin-left:0.5rem;color:#4ECD64;'>âœ… Detectada</span></button>` :
                        `<button data-wallet="unisat" data-action="install" style="padding: 1rem; font-size: 1.1rem; border-radius: 12px; font-weight: 600; cursor: not-allowed; background: #eee; color: #aaa; border: none;">ğŸ¦„ UniSat <span style='margin-left:0.5rem;color:#E55A2B;'>âŒ No instalada</span></button>`
                    }
                    ${availableWallets.okx.available ?
                        `<button data-wallet="okx" style="padding: 1rem; font-size: 1.1rem; border-radius: 12px; font-weight: 600; cursor: pointer; background: var(--gradient-primary); color: var(--dark); border: none;">ğŸŸ  OKX <span style='margin-left:0.5rem;color:#4ECD64;'>âœ… Detectada</span></button>` :
                        `<button data-wallet="okx" data-action="install" style="padding: 1rem; font-size: 1.1rem; border-radius: 12px; font-weight: 600; cursor: not-allowed; background: #eee; color: #aaa; border: none;">ğŸŸ  OKX <span style='margin-left:0.5rem;color:#E55A2B;'>âŒ No instalada</span></button>`
                    }
                </div>
                <button data-action="cancel" style="margin-top: 1rem; padding: 0.75rem 1.5rem; border-radius: 12px; font-weight: 600; cursor: pointer; background: #fff; color: var(--dark); border: 1px solid #eee;">Cancelar</button>
            </div>
        `;
        // Click handler
        modal.addEventListener('click', (event) => {
            const button = event.target.closest('button');
            if (!button) return;
            const walletType = button.dataset.wallet;
            const action = button.dataset.action;
            if (action === 'install') {
                const urls = { unisat: 'https://unisat.io/', okx: 'https://okx.com/web3' };
                window.open(urls[walletType], '_blank');
                return;
            }
            if (action === 'cancel') {
                modal.remove();
                resolve(null);
                return;
            }
            if (walletType && !button.hasAttribute('data-action')) {
                modal.remove();
                resolve(walletType);
            }
        });
        document.body.appendChild(modal);
    });
}

// Export to window
window.showWalletModalClean = showWalletModalClean;

