/* Wallet Modal Styles */
.wallet-selector-modal {
    z-index: 99999;
    position: fixed;
    top: 0;
    left: 0;
    width: 100vw;
    height: 100vh;
    display: flex;
    align-items: center;
    justify-content: center;
    background: rgba(10, 14, 26, 0.85);
}

.wallet-modal-content {
    background: var(--gradient-glass);
    border-radius: 24px;
    padding: 2rem;
    max-width: 400px;
    width: 90%;
    text-align: center;
    box-shadow: 0 8px 32px rgba(0, 0, 0, 0.25);
}

.wallet-buttons-container {
    display: flex;
    flex-direction: column;
    gap: 1rem;
    margin: 1.5rem 0;
}

.wallet-btn {
    padding: 1rem;
    font-size: 1.1rem;
    border-radius: 12px;
    font-weight: 600;
    cursor: pointer;
    background: var(--gradient-primary);
    color: var(--dark);
    border: none;
}

.wallet-btn.disabled {
    cursor: not-allowed;
    background: #eee;
    color: #aaa;
}

.wallet-status-available {
    margin-left: 0.5rem;
    color: #4ECD64;
}

.wallet-status-unavailable {
    margin-left: 0.5rem;
    color: #E55A2B;
}

.wallet-cancel-btn {
    margin-top: 1rem;
    padding: 0.75rem 1.5rem;
    border-radius: 12px;
    font-weight: 600;
    cursor: pointer;
    background: #fff;
    color: var(--dark);
    border: 1px solid #eee;
}

