}

// Verificar depÃ³sitos pendientes periÃ³dicamente
function startDepositCheck() {
    // Verificar depÃ³sitos cada 30 segundos
    setInterval(async () => {
        await checkPendingDeposits();
    }, 30000);

    // Verificar inmediatamente
