/*
 * ===== WALLET MANAGER =====
 * 
 * This module handles low-level wallet operations for Bitcoin wallets.
 * It provides a unified interface for detecting, connecting, and managing
 * different wallet types (UniSat, OKX) without any UI dependencies.
 * 
 * All methods return a standardized response format:
 * { success: boolean, data?: any, error?: string }
 */

const Wallets = {
    // Current wallet state
    currentWallet: null,
    currentAddress: null,
    currentType: null,

    /**
     * Detects available Bitcoin wallets in the browser
     * @returns {Object} Object with wallet availability status
     */
    async detectAvailableWallets() {
        console.log('ğŸ” Detecting available wallets...');

        try {
            const wallets = {
                unisat: {
                    available: false,
                    installed: false,
                    name: 'UniSat',
                    icon: 'ğŸ¦„'
                },
                okx: {
                    available: false,
                    installed: false,
                    name: 'OKX',
                    icon: 'ğŸŸ '
                }
            };

            // Check UniSat wallet
            if (typeof window.unisat !== 'undefined' && window.unisat) {
                wallets.unisat.installed = true;
                wallets.unisat.available = typeof window.unisat.requestAccounts === 'function';
                console.log('âœ… UniSat wallet detected');
            } else {
                console.log('âŒ UniSat wallet not found');
            }

            // Check OKX wallet
            if (typeof window.okxwallet !== 'undefined' && window.okxwallet) {
                wallets.okx.installed = true;
                wallets.okx.available = typeof window.okxwallet.bitcoin?.requestAccounts === 'function';
                console.log('âœ… OKX wallet detected');
            } else {
                console.log('âŒ OKX wallet not found');
            }

            console.log('ğŸ” Wallet detection complete:', wallets);
            return { success: true, data: wallets };

        } catch (error) {
            console.error('âŒ Error detecting wallets:', error);
            return { success: false, error: error.message };
        }
    },

    /**
     * Connects to UniSat wallet
     * @returns {Object} Connection result with address and signature
     */
    async connectUniSat() {
        console.log('ğŸ¦„ Connecting to UniSat wallet...');

        try {
            // Check if UniSat is available
            if (!window.unisat) {
                console.error('âŒ UniSat wallet not found');
                return { success: false, error: 'UniSat wallet not installed' };
            }

            if (typeof window.unisat.requestAccounts !== 'function') {
                console.error('âŒ UniSat wallet methods not available');
                return { success: false, error: 'UniSat wallet not properly initialized' };
            }

            // Request account access
            console.log('ğŸ” Requesting UniSat account access...');
            const accounts = await window.unisat.requestAccounts();

            if (!accounts || accounts.length === 0) {
                console.error('âŒ No accounts returned from UniSat');
                return { success: false, error: 'No accounts available' };
            }

            const address = accounts[0];
            console.log('âœ… UniSat account connected:', address);

            // Get balance
            console.log('ğŸ’° Getting UniSat balance...');
            const balance = await window.unisat.getBalance();
            console.log('ğŸ’° UniSat balance:', balance);

            // Create signature message
            const message = `MoonYetis Slots Login - ${Date.now()}`;
            console.log('âœï¸ Requesting signature for message:', message);

            const signature = await window.unisat.signMessage(message);
            console.log('âœ… Signature received from UniSat');

            // Store current wallet state
            this.currentWallet = window.unisat;
            this.currentAddress = address;
            this.currentType = 'unisat';

            const result = {
                address,
                walletType: 'unisat',
                balance: {
                    confirmed: balance.confirmed || 0,
                    unconfirmed: balance.unconfirmed || 0,
                    total: balance.total || 0
                },
                signature: {
                    signature,
                    message,
                    timestamp: Date.now()
                }
            };

            console.log('âœ… UniSat connection successful:', result);
            return { success: true, data: result };

        } catch (error) {
            console.error('âŒ UniSat connection failed:', error);

            // Development mode fallback
            if (this._isDevelopmentMode()) {
                console.log('ğŸš§ Development mode: Creating fake UniSat connection');
                return this._createFakeConnection('unisat');
            }

            return { success: false, error: error.message };
        }
    },

    /**
     * Connects to OKX wallet
     * @returns {Object} Connection result with address and signature
     */
    async connectOKX() {
        console.log('ğŸŸ  Connecting to OKX wallet...');

        try {
            // Check if OKX is available
            if (!window.okxwallet) {
                console.error('âŒ OKX wallet not found');
                return { success: false, error: 'OKX wallet not installed' };
            }

            if (!window.okxwallet.bitcoin || typeof window.okxwallet.bitcoin.requestAccounts !== 'function') {
                console.error('âŒ OKX wallet Bitcoin methods not available');
                return { success: false, error: 'OKX wallet not properly initialized' };
            }

            // Request account access
            console.log('ğŸ” Requesting OKX account access...');
            const accounts = await window.okxwallet.bitcoin.requestAccounts();

            if (!accounts || accounts.length === 0) {
                console.error('âŒ No accounts returned from OKX');
                return { success: false, error: 'No accounts available' };
            }

            const address = accounts[0];
            console.log('âœ… OKX account connected:', address);

            // Get balance
            console.log('ğŸ’° Getting OKX balance...');
            const balance = await window.okxwallet.bitcoin.getBalance();
            console.log('ğŸ’° OKX balance:', balance);

            // Create signature message
            const message = `MoonYetis Slots Login - ${Date.now()}`;
            console.log('âœï¸ Requesting signature for message:', message);

            const signature = await window.okxwallet.bitcoin.signMessage(message);
            console.log('âœ… Signature received from OKX');

            // Store current wallet state
            this.currentWallet = window.okxwallet.bitcoin;
            this.currentAddress = address;
            this.currentType = 'okx';

            const result = {
                address,
                walletType: 'okx',
                balance: {
                    confirmed: balance.confirmed || 0,
                    unconfirmed: balance.unconfirmed || 0,
                    total: balance.total || 0
                },
                signature: {
                    signature,
                    message,
                    timestamp: Date.now()
                }
            };

            console.log('âœ… OKX connection successful:', result);
            return { success: true, data: result };

        } catch (error) {
            console.error('âŒ OKX connection failed:', error);

            // Development mode fallback
            if (this._isDevelopmentMode()) {
                console.log('ğŸš§ Development mode: Creating fake OKX connection');
                return this._createFakeConnection('okx');
            }

            return { success: false, error: error.message };
        }
    },

    /**
     * Disconnects the current wallet
     * @returns {Object} Disconnection result
     */
    async disconnect() {
        console.log('ğŸ”Œ Disconnecting wallet...');

        try {
            if (this.currentWallet && this.currentType) {
                console.log(`ğŸ”Œ Disconnecting ${this.currentType} wallet`);

                // Clear current state
                this.currentWallet = null;
                this.currentAddress = null;
                this.currentType = null;

                console.log('âœ… Wallet disconnected successfully');
                return { success: true, data: { message: 'Wallet disconnected' } };
            } else {
                console.log('âš ï¸ No wallet to disconnect');
                return { success: true, data: { message: 'No wallet connected' } };
            }
        } catch (error) {
            console.error('âŒ Error disconnecting wallet:', error);
            return { success: false, error: error.message };
        }
    },

    /**
     * Gets the current wallet state
     * @returns {Object} Current wallet information
     */
    getCurrent() {
        console.log('ğŸ“Š Getting current wallet state...');

        try {
            if (!this.currentWallet || !this.currentAddress || !this.currentType) {
                console.log('ğŸ“Š No wallet currently connected');
                return { success: true, data: null };
            }

            const current = {
                address: this.currentAddress,
                type: this.currentType,
                connected: true
            };

            console.log('ğŸ“Š Current wallet state:', current);
