/*
 * ===== WALLET CONNECTION FLOW =====
 * 
 * This module orchestrates the complete wallet connection process.
 * It coordinates between wallet detection, user selection, connection,
 * and state management without handling low-level wallet operations or UI.
 * 
 * FLOW:
 * 1. Detect available wallets
 * 2. Show wallet selection modal
 * 3. Connect to selected wallet
 * 4. Update global state and UI on success
 * 5. Handle errors gracefully
 * 
 * DEPENDENCIES:
 * - Wallets: Low-level wallet operations
 * - showWalletModal: Modal for wallet selection
 * - showMessage: User feedback function
 * - gameState: Global state object
 * - updateWalletUI: UI update function
 */

/**
 * Main wallet connection flow handler
 * Orchestrates the complete process from detection to connection
 * @returns {Promise<Object>} Connection result
 */
async function handleWalletConnection() {
    console.log('ğŸ”— Starting wallet connection flow...');

    try {
        // Step 1: Detect available wallets
        console.log('ğŸ” Step 1: Detecting available wallets...');
        showMessage('ğŸ” Detectando wallets disponibles...', 'neutral');

        const detectionResult = await Wallets.detectAvailableWallets();

        if (!detectionResult.success) {
            console.error('âŒ Wallet detection failed:', detectionResult.error);
            showMessage(`âŒ Error detectando wallets: ${detectionResult.error}`, 'error');
            return { success: false, error: detectionResult.error };
        }

        const availableWallets = detectionResult.data;
        console.log('âœ… Wallets detected:', availableWallets);

        // Check if any wallets are available
        if (!availableWallets.unisat.available && !availableWallets.okx.available) {
            console.warn('âš ï¸ No wallets available');
            showMessage('âš ï¸ No se encontraron wallets disponibles. Instala UniSat o OKX.', 'warning');
            return { success: false, error: 'No wallets available' };
        }

        // Step 2: Show wallet selection modal
        console.log('ğŸ” Step 2: Showing wallet selection modal...');
        showMessage('ğŸ”— Selecciona tu wallet preferida...', 'neutral');

        const walletChoice = await showWalletModal(availableWallets);

        if (!walletChoice) {
            console.log('â„¹ï¸ User cancelled wallet selection');
            showMessage('âš ï¸ ConexiÃ³n cancelada', 'warning');
            return { success: false, error: 'User cancelled' };
        }

        console.log('âœ… User selected wallet:', walletChoice);

        // Step 3: Connect to selected wallet
        console.log(`ğŸ” Step 3: Connecting to ${walletChoice} wallet...`);
        showMessage(`ğŸ”„ Conectando a ${walletChoice.toUpperCase()}... Autoriza en la extensiÃ³n`, 'loading');

        let connectionResult;

        if (walletChoice === 'unisat') {
            connectionResult = await Wallets.connectUniSat();
        } else if (walletChoice === 'okx') {
            connectionResult = await Wallets.connectOKX();
        } else {
            console.error('âŒ Invalid wallet choice:', walletChoice);
            showMessage('âŒ SelecciÃ³n de wallet invÃ¡lida', 'error');
            return { success: false, error: 'Invalid wallet choice' };
        }

        if (!connectionResult.success) {
            console.error('âŒ Wallet connection failed:', connectionResult.error);
            showMessage(`âŒ Error conectando: ${connectionResult.error}`, 'error');
            return { success: false, error: connectionResult.error };
        }

        const walletData = connectionResult.data;
        console.log('âœ… Wallet connected successfully:', walletData);

        // Step 4: Verify connection with backend (if needed)
        console.log('ğŸ” Step 4: Verifying connection with backend...');
        showMessage('ğŸ” Verificando conexiÃ³n con servidor...', 'loading');

        try {
            const verificationResult = await verifyWalletWithBackend(walletData);

            if (!verificationResult.success) {
                console.error('âŒ Backend verification failed:', verificationResult.error);
                showMessage(`âŒ Error verificando: ${verificationResult.error}`, 'error');
                return { success: false, error: verificationResult.error };
            }

            console.log('âœ… Backend verification successful');

        } catch (error) {
            console.error('âŒ Backend verification error:', error);
            showMessage(`âŒ Error de verificaciÃ³n: ${error.message}`, 'error');
            return { success: false, error: error.message };
        }

        // Step 5: Update global state and UI
        console.log('ğŸ” Step 5: Updating global state and UI...');
        showMessage('âœ… Actualizando estado del juego...', 'loading');

        try {
            // Update global game state
            if (typeof gameState !== 'undefined') {
                gameState.connectedWallet = true;
                gameState.walletAddress = walletData.address;
                gameState.walletType = walletData.walletType;
                gameState.balance = walletData.balance.total;
                console.log('âœ… Game state updated');
            }

            // Update UI components
            if (typeof updateWalletUI === 'function') {
                updateWalletUI(walletData);
                console.log('âœ… Wallet UI updated');
            }

            // Update slot machine UI
            if (typeof slotMachine !== 'undefined' && slotMachine.updateUI) {
                slotMachine.updateUI();
                console.log('âœ… Slot machine UI updated');
            }

            // Setup wallet event listeners
            if (typeof setupWalletEventListeners === 'function') {
                setupWalletEventListeners();
                console.log('âœ… Wallet event listeners setup');
            }

            // Register for deposit monitoring
            if (typeof registerForDepositMonitoring === 'function') {
                await registerForDepositMonitoring();
                console.log('âœ… Deposit monitoring registered');
            }

        } catch (error) {
            console.error('âŒ Error updating state/UI:', error);
            showMessage(`âŒ Error actualizando interfaz: ${error.message}`, 'error');
            return { success: false, error: error.message };
        }

        // Step 6: Show success message
        console.log('âœ… Wallet connection flow completed successfully');
        showMessage(`ğŸ‰ Wallet ${walletData.walletType.toUpperCase()} conectada exitosamente!`, 'success');

        return { 
            success: true, 
            data: {
                address: walletData.address,
                walletType: walletData.walletType,
                balance: walletData.balance
            }
        };

    } catch (error) {
        console.error('âŒ Unexpected error in wallet connection flow:', error);
        showMessage(`âŒ Error inesperado: ${error.message}`, 'error');
        return { success: false, error: error.message };
    }
}

/**
 * Handles wallet disconnection
 * @returns {Promise<Object>} Disconnection result
 */
async function handleWalletDisconnection() {
    console.log('ğŸ”Œ Starting wallet disconnection flow...');

    try {
        showMessage('ğŸ”„ Desconectando wallet...', 'loading');

        // Disconnect from wallet
        const disconnectionResult = await Wallets.disconnect();

        if (!disconnectionResult.success) {
            console.error('âŒ Wallet disconnection failed:', disconnectionResult.error);
            showMessage(`âŒ Error desconectando: ${disconnectionResult.error}`, 'error');
            return { success: false, error: disconnectionResult.error };
        }

        // Update global state
        if (typeof gameState !== 'undefined') {
            gameState.connectedWallet = false;
            gameState.walletAddress = null;
            gameState.walletType = null;
            gameState.balance = 0;
            console.log('âœ… Game state cleared');
        }

        // Update UI
        if (typeof updateDisconnectedWalletUI === 'function') {
            updateDisconnectedWalletUI();
            console.log('âœ… Wallet UI reset');
        }

        // Update slot machine UI
        if (typeof slotMachine !== 'undefined' && slotMachine.updateUI) {
            slotMachine.updateUI();
            console.log('âœ… Slot machine UI updated');
        }

        console.log('âœ… Wallet disconnection flow completed successfully');
        showMessage('ğŸ”Œ Wallet desconectada exitosamente', 'success');

        return { success: true, data: { message: 'Wallet disconnected' } };

    } catch (error) {
        console.error('âŒ Unexpected error in wallet disconnection flow:', error);
        showMessage(`âŒ Error inesperado: ${error.message}`, 'error');
        return { success: false, error: error.message };
    }
}

/**
 * Gets current wallet status
 * @returns {Object} Current wallet information
 */
function getCurrentWalletStatus() {
    console.log('ğŸ“Š Getting current wallet status...');

    try {
        const currentResult = Wallets.getCurrent();

        if (!currentResult.success) {
            console.error('âŒ Error getting wallet status:', currentResult.error);
            return { success: false, error: currentResult.error };
        }

        const isConnected = currentResult.data !== null;
        console.log('ğŸ“Š Wallet status:', { connected: isConnected, data: currentResult.data });

        return { 
            success: true, 
            data: { 
                connected: isConnected, 
                wallet: currentResult.data 
            }
        };

    } catch (error) {
        console.error('âŒ Error getting wallet status:', error);
        return { success: false, error: error.message };
    }
}

// Export functions to window
window.handleWalletConnection = handleWalletConnection;
window.handleWalletDisconnection = handleWalletDisconnection;
window.getCurrentWalletStatus = getCurrentWalletStatus;

console.log('âœ… Wallet Flow module loaded');

