/*
 * ===== WALLET MODAL STYLES =====
 * 
 * Modern, responsive and accessible styles for wallet selection modal.
 * Includes smooth animations, good contrast ratios, and mobile-friendly design.
 */

/* CSS Variables for theming */
:root {
  /* Modal Colors */
  --modal-overlay: rgba(0, 0, 0, 0.75);
  --modal-bg: #ffffff;
  --modal-border: #e5e7eb;
  --modal-shadow: 0 25px 50px -12px rgba(0, 0, 0, 0.25);

  /* Text Colors */
  --text-primary: #111827;
  --text-secondary: #6b7280;
  --text-success: #059669;
  --text-error: #dc2626;

  /* Button Colors */
  --button-primary: #3b82f6;
  --button-primary-hover: #2563eb;
  --button-secondary: #f3f4f6;
  --button-secondary-hover: #e5e7eb;
  --button-disabled: #f9fafb;
  --button-text-disabled: #9ca3af;

  /* Spacing */
  --modal-padding: 2rem;
  --modal-gap: 1.5rem;
  --button-padding: 1rem 1.5rem;
  --border-radius: 0.75rem;
  --border-radius-lg: 1rem;

  /* Animations */
  --animation-duration: 0.3s;
  --animation-easing: cubic-bezier(0.16, 1, 0.3, 1);
}

/* Dark mode support */
@media (prefers-color-scheme: dark) {
  :root {
    --modal-bg: #1f2937;
    --modal-border: #374151;
    --text-primary: #f9fafb;
    --text-secondary: #9ca3af;
    --button-secondary: #374151;
    --button-secondary-hover: #4b5563;
    --button-disabled: #374151;
  }
}

/* Modal Overlay */
.wallet-selector-modal {
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  z-index: 9999;
  display: flex;
  align-items: center;
  justify-content: center;
  background: var(--modal-overlay);
  backdrop-filter: blur(4px);
  padding: 1rem;

  /* Animation */
  opacity: 0;
  animation: fadeIn var(--animation-duration) var(--animation-easing) forwards;
}

@keyframes fadeIn {
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
}

/* Modal Content */
.wallet-modal-content {
  background: var(--modal-bg);
  border: 1px solid var(--modal-border);
  border-radius: var(--border-radius-lg);
  box-shadow: var(--modal-shadow);
  padding: var(--modal-padding);
  width: 100%;
  max-width: 400px;
  max-height: 90vh;
  overflow-y: auto;
  text-align: center;

  /* Animation */
  transform: scale(0.9) translateY(20px);
  animation: slideIn var(--animation-duration) var(--animation-easing) forwards;
}

@keyframes slideIn {
  from {
    transform: scale(0.9) translateY(20px);
    opacity: 0;
  }
  to {
    transform: scale(1) translateY(0);
    opacity: 1;
  }
}

/* Modal Header */
.wallet-modal-content h3 {
  color: var(--text-primary);
  font-size: 1.5rem;
  font-weight: 600;
  margin: 0 0 0.5rem 0;
  line-height: 1.3;
}

.wallet-modal-content p {
  color: var(--text-secondary);
  font-size: 0.95rem;
  margin: 0 0 var(--modal-gap) 0;
  line-height: 1.5;
}

/* Wallet Buttons Container */
.wallet-buttons-container {
  display: flex;
  flex-direction: column;
  gap: 1rem;
  margin: var(--modal-gap) 0;
}

/* Wallet Button Base Styles */
.wallet-btn {
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 0.75rem;
  padding: var(--button-padding);
  font-size: 1rem;
  font-weight: 500;
  line-height: 1.5;
  border: 2px solid transparent;
  border-radius: var(--border-radius);
  cursor: pointer;
  transition: all var(--animation-duration) var(--animation-easing);
  text-decoration: none;
  min-height: 3.5rem;

  /* Focus styles for accessibility */
  &:focus {
    outline: none;
    border-color: var(--button-primary);
    box-shadow: 0 0 0 3px rgba(59, 130, 246, 0.1);
  }
}

/* Available Wallet Button */
.wallet-btn:not(.disabled) {
  background: var(--button-primary);
  color: white;

  &:hover {
    background: var(--button-primary-hover);
    transform: translateY(-1px);
    box-shadow: 0 4px 12px rgba(59, 130, 246, 0.15);
  }

  &:active {
    transform: translateY(0);
  }
}

/* Disabled Wallet Button */
.wallet-btn.disabled {
  background: var(--button-disabled);
  color: var(--button-text-disabled);
  cursor: not-allowed;
  border-color: var(--modal-border);

  &:hover {
    transform: none;
    box-shadow: none;
  }
}

/* Wallet Status Indicators */
.wallet-status-available {
  display: inline-flex;
  align-items: center;
  gap: 0.25rem;
  font-size: 0.875rem;
  color: var(--text-success);
  font-weight: 500;
}

.wallet-status-unavailable {
  display: inline-flex;
  align-items: center;
  gap: 0.25rem;
  font-size: 0.875rem;
  color: var(--text-error);
  font-weight: 500;
}

/* Cancel Button */
.wallet-cancel-btn {
  background: var(--button-secondary);
  color: var(--text-primary);
  border: 1px solid var(--modal-border);
  border-radius: var(--border-radius);
  padding: 0.75rem 1.5rem;
  font-size: 0.95rem;
  font-weight: 500;
  cursor: pointer;
  transition: all var(--animation-duration) var(--animation-easing);
  margin-top: 1rem;

  &:hover {
    background: var(--button-secondary-hover);
    transform: translateY(-1px);
  }

  &:focus {
    outline: none;
    border-color: var(--button-primary);
    box-shadow: 0 0 0 3px rgba(59, 130, 246, 0.1);
  }

  &:active {
    transform: translateY(0);
  }
}

/* Responsive Design */
@media (max-width: 640px) {
  .wallet-selector-modal {
    padding: 0.75rem;
  }

  .wallet-modal-content {
    padding: 1.5rem;
    max-width: none;
  }

  .wallet-modal-content h3 {
    font-size: 1.25rem;
  }

  .wallet-btn {
    padding: 1rem;
    font-size: 0.95rem;
    min-height: 3rem;
  }

  .wallet-buttons-container {
    gap: 0.75rem;
  }
}

/* Reduced Motion Support */
@media (prefers-reduced-motion: reduce) {
  .wallet-selector-modal,
  .wallet-modal-content,
  .wallet-btn,
  .wallet-cancel-btn {
    animation: none;
    transition: none;
  }

  .wallet-btn:hover,
  .wallet-cancel-btn:hover {
    transform: none;
  }
}

/* High Contrast Mode Support */
@media (prefers-contrast: high) {
  :root {
    --modal-border: #000000;
    --button-primary: #0000ff;
    --button-primary-hover: #000080;
    --text-primary: #000000;
    --text-secondary: #000000;
  }

  .wallet-btn {
    border: 2px solid currentColor;
  }

  .wallet-btn:focus {
    outline: 3px solid currentColor;
    outline-offset: 2px;
  }
}

/* Loading State */
.wallet-btn.loading {
  position: relative;
  color: transparent;
  pointer-events: none;
}

.wallet-btn.loading::after {
  content: '';
  position: absolute;
  top: 50%;
  left: 50%;
  width: 1.25rem;
  height: 1.25rem;
  margin: -0.625rem 0 0 -0.625rem;
  border: 2px solid transparent;
  border-top: 2px solid currentColor;
  border-radius: 50%;
  animation: spin 1s linear infinite;
}

@keyframes spin {
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
}

/* Wallet Icons */
.wallet-icon {
  font-size: 1.5rem;
  flex-shrink: 0;
}

/* Success/Error States */
.wallet-btn.success {
  background: var(--text-success);
  color: white;
}

.wallet-btn.error {
  background: var(--text-error);
  color: white;
}

