                // Registrar para monitoreo de depÃ³sitos
                await registerForDepositMonitoring();

                showMessage(`ğŸ‰ Wallet ${connectionResult.walletType.toUpperCase()} conectada!`, 'win');
                console.log('âœ… Wallet conectada exitosamente:', connectionResult.address);
            } else {
