    expires 1h;
    add_header Cache-Control "no-cache, must-revalidate";
}

# Guarda y cierra el archivo (Ctrl+O, Enter, Ctrl+X)

# Recarga Nginx para aplicar los cambios
sudo systemctl reload nginx

