     1â†’// Slot Machine - Game logic and visual effects
     2â†’console.log('ğŸ° Slot Machine Loading...');
     3â†’
     4â†’// Ultra-Accessible Number Formatting Utilities with Performance Cache
     5â†’const NumberFormatter = {
     6â†’    // Cache for performance optimization
     7â†’    _tokenFormatCache: new Map(),
     8â†’    _usdFormatCache: new Map(),
     9â†’    
    10â†’    // Format token amount for display (with cache)
    11â†’    formatTokenAmount: (amount) => {
    12â†’        // Check cache first
    13â†’        if (NumberFormatter._tokenFormatCache.has(amount)) {
    14â†’            return NumberFormatter._tokenFormatCache.get(amount);
    15â†’        }
    16â†’        
    17â†’        let result;
    18â†’        if (amount >= 1000000000) {
    19â†’            result = (amount / 1000000000).toFixed(1) + 'B MY';
    20â†’        } else if (amount >= 1000000) {
    21â†’            // Handle edge case: 999,500,000 should be "999.5M MY" not "1000M MY"
    22â†’            const millions = amount / 1000000;
    23â†’            if (millions >= 999.5) {
    24â†’                result = (amount / 1000000000).toFixed(2) + 'B MY';
    25â†’            } else {
    26â†’                result = millions.toFixed(1) + 'M MY';
    27â†’            }
    28â†’        } else if (amount >= 1000) {
    29â†’            // Handle edge case: 999,500 should be "999K MY" not "1000K MY"
    30â†’            const thousands = amount / 1000;
    31â†’            if (thousands >= 999.5) {
    32â†’                result = (amount / 1000000).toFixed(2) + 'M MY';
    33â†’            } else {
    34â†’                result = thousands.toFixed(0) + 'K MY';
    35â†’            }
    36â†’        } else {
    37â†’            result = amount.toLocaleString() + ' MY';
    38â†’        }
    39â†’        
    40â†’        // Cache result (limit cache size to prevent memory issues)
    41â†’        if (NumberFormatter._tokenFormatCache.size < 1000) {
    42â†’            NumberFormatter._tokenFormatCache.set(amount, result);
    43â†’        }
    44â†’        
    45â†’        return result;
    46â†’    },
    47â†’
    48â†’    // Format USD equivalent (optimized for ultra-micro amounts with cache)
    49â†’    formatUSDEquivalent: (tokenAmount, rate = 0.0000001037) => {
    50â†’        // Create cache key
    51â†’        const cacheKey = `${tokenAmount}_${rate}`;
    52â†’        
    53â†’        // Check cache first
    54â†’        if (NumberFormatter._usdFormatCache.has(cacheKey)) {
    55â†’            return NumberFormatter._usdFormatCache.get(cacheKey);
    56â†’        }
    57â†’        
    58â†’        const usdValue = tokenAmount * rate;
    59â†’        let result;
    60â†’        
    61â†’        if (usdValue < 0.0001) {
    62â†’            // Ultra-micro amounts: show as fractions of cents (â€° = per-mille = 1/1000)
    63â†’            result = ` (~${(usdValue * 10000).toFixed(1)}â€°)`;
    64â†’        } else if (usdValue < 0.001) {
    65â†’            // Very small amounts: show as 10ths of cents
    66â†’            result = ` (~${(usdValue * 1000).toFixed(1)}â‚â‚€Â¢)`;
    67â†’        } else if (usdValue < 0.01) {
    68â†’            // Small amounts: show as cents with 1 decimal
    69â†’            result = ` (~${(usdValue * 100).toFixed(1)}Â¢)`;
    70â†’        } else if (usdValue < 1) {
    71â†’            // Sub-dollar amounts: show as cents
    72â†’            result = ` (~${(usdValue * 100).toFixed(0)}Â¢)`;
    73â†’        } else {
    74â†’            result = ` (~$${usdValue.toFixed(2)})`;
    75â†’        }
    76â†’        
    77â†’        // Cache result (limit cache size to prevent memory issues)
    78â†’        if (NumberFormatter._usdFormatCache.size < 1000) {
    79â†’            NumberFormatter._usdFormatCache.set(cacheKey, result);
    80â†’        }
    81â†’        
    82â†’        return result;
    83â†’    },
    84â†’
    85â†’    // Clear cache periodically for memory management
    86â†’    clearCache: () => {
    87â†’        NumberFormatter._tokenFormatCache.clear();
    88â†’        NumberFormatter._usdFormatCache.clear();
    89â†’    },
    90â†’
    91â†’    // Cache statistics for debugging
    92â†’    getCacheStats: () => {
    93â†’        return {
    94â†’            tokenCacheSize: NumberFormatter._tokenFormatCache.size,
    95â†’            usdCacheSize: NumberFormatter._usdFormatCache.size
    96â†’        };
    97â†’    },
    98â†’
    99â†’    // Format combined display (token + USD)
   100â†’    formatCombined: (tokenAmount, rate = 0.0000001037) => {
   101â†’        return NumberFormatter.formatTokenAmount(tokenAmount) + NumberFormatter.formatUSDEquivalent(tokenAmount, rate);
   102â†’    }
   103â†’};
   104â†’
   105â†’// ConfiguraciÃ³n del juego - usando imÃ¡genes reales
   106â†’const SYMBOLS = [
   107â†’    { id: 'yeti', name: 'Yeti Wild', image: 'assets/symbols/yeti-wild.png', emoji: 'ğŸ”ï¸' },
   108â†’    { id: 'rocket', name: 'Rocket High', image: 'assets/symbols/rocket-high.png', emoji: 'ğŸš€' },
   109â†’    { id: 'moon', name: 'Moon Scatter', image: 'assets/symbols/moon-scatter.png', emoji: 'ğŸŒ™' },
   110â†’    { id: 'coin', name: 'Coin Medium', image: 'assets/symbols/coin-medium.png', emoji: 'ğŸª™' },
   111â†’    { id: 'star', name: 'Star Medium', image: 'assets/symbols/star-medium.png', emoji: 'â­' },
   112â†’    { id: 'planet', name: 'Planet Low', image: 'assets/symbols/planet-low.png', emoji: 'ğŸª' },
   113â†’    { id: 'alien', name: 'Alien Bonus', image: 'assets/symbols/alien-bonus.png', emoji: 'ğŸ‘½' },
   114â†’    { id: 'ufo', name: 'UFO Special', image: 'assets/symbols/ufo-special.png', emoji: 'ğŸ›¸' }
   115â†’];
   116â†’
   117â†’// Array de IDs para compatibilidad con cÃ³digo existente
   118â†’const SYMBOL_IDS = SYMBOLS.map(s => s.id);
   119â†’
   120â†’// Estado del juego - Ultra-Accessible Limits
   121â†’let gameState = {
   122â†’    balance: 0,
   123â†’    currentBet: 100000, // Default bet 100K MOONYETIS (~$0.01 current, $1 if 100x)
   124â†’    activeLines: 25,
   125â†’    isSpinning: false,
   126â†’    connectedWallet: false,
   127â†’    totalSpins: 0,
   128â†’    totalWagered: 0,
   129â†’    totalWon: 0,
   130â†’    biggestWin: 0,
   131â†’    // Ultra-accessible betting limits
   132â†’    minBet: 10000,        // 10K MOONYETIS (~$0.001 current)
   133â†’    maxBet: 50000000,     // 50M MOONYETIS (~$5.18 current)
   134â†’    maxTotalBet: 250000000, // 250M MOONYETIS total (~$25.9 current)
   135â†’    popularBets: [10000, 50000, 100000, 500000, 1000000, 5000000], // Ultra-scalable range
   136â†’    usdRate: 0.0000001037 // Current USD rate per MOONYETIS
   137â†’};
   138â†’
   139â†’// Slot Machine Manager
   140â†’const slotMachine = {
   141â†’    // Initialize game
   142â†’    init() {
   143â†’        console.log('ğŸ° Inicializando MoonYetis Slots...');
   144â†’        this.createReels();
   145â†’        this.updateUI();
   146â†’        this.setupKeyboardControls();
   147â†’        console.log('âœ… Juego inicializado correctamente');
   148â†’    },
   149â†’
   150â†’    // Crear rodillos
   151â†’    createReels() {
   152â†’        const container = document.getElementById('reelsContainer');
   153â†’        if (!container) {
   154â†’            console.error('âŒ No se encontrÃ³ el contenedor de rodillos');
   155â†’            return;
   156â†’        }
   157â†’        
   158â†’        container.innerHTML = '';
   159â†’
   160â†’        for (let i = 0; i < 5; i++) {
   161â†’            const reel = document.createElement('div');
   162â†’            reel.className = 'reel';
   163â†’            reel.id = `reel-${i}`;
   164â†’
   165â†’            for (let j = 0; j < 3; j++) {
   166â†’                const symbol = document.createElement('div');
   167â†’                symbol.className = 'symbol';
   168â†’                
   169â†’                // Seleccionar sÃ­mbolo aleatorio
   170â†’                const randomSymbol = SYMBOLS[Math.floor(Math.random() * SYMBOLS.length)];
   171â†’                
   172â†’                // Crear imagen
   173â†’                const img = document.createElement('img');
   174â†’                img.src = randomSymbol.image;
   175â†’                img.alt = randomSymbol.name;
   176â†’                img.className = 'symbol-image';
   177â†’                img.setAttribute('data-symbol-id', randomSymbol.id);
   178â†’                
   179â†’                // Fallback a emoji si la imagen no carga
   180â†’                img.onerror = function() {
   181â†’                    symbol.innerHTML = randomSymbol.emoji;
   182â†’                    symbol.classList.add('symbol-emoji');
   183â†’                };
   184â†’                
   185â†’                symbol.appendChild(img);
   186â†’                reel.appendChild(symbol);
   187â†’            }
   188â†’
   189â†’            container.appendChild(reel);
   190â†’        }
   191â†’        console.log('ğŸ² Rodillos creados correctamente');
   192â†’    },
   193â†’
   194â†’    // FunciÃ³n principal de giro
   195â†’    spin() {
   196â†’        // Allow demo mode or wallet mode
   197â†’        if (!gameState.connectedWallet && gameState.balance === 0) {
   198â†’            this.showMessage('âš ï¸ Â¡Conecta tu wallet o usa modo demo!', 'lose');
   199â†’            return;
   200â†’        }
   201â†’
   202â†’        if (gameState.isSpinning) {
   203â†’            console.log('âš ï¸ Ya hay un giro en progreso');
   204â†’            return;
   205â†’        }
   206â†’
   207â†’        const totalBet = gameState.currentBet * gameState.activeLines;
   208â†’        
   209â†’        if (gameState.balance < totalBet) {
   210â†’            this.showMessage('ğŸ’¸ Â¡Saldo insuficiente!', 'lose');
   211â†’            return;
   212â†’        }
   213â†’
   214â†’        console.log('ğŸ° Iniciando giro...');
   215â†’        gameState.isSpinning = true;
   216â†’        gameState.balance -= totalBet;
   217â†’        gameState.totalSpins++;
   218â†’        gameState.totalWagered += totalBet;
   219â†’        
   220â†’        this.updateUI();
   221â†’
   222â†’        // Animar rodillos
   223â†’        const reels = document.querySelectorAll('.reel');
   224â†’        reels.forEach(function(reel) {
   225â†’            reel.classList.add('spinning');
   226â†’        });
   227â†’
   228â†’        // Simular tiempo de giro
   229â†’        setTimeout(() => {
   230â†’            reels.forEach(function(reel) {
   231â†’                reel.classList.remove('spinning');
   232â†’            });
   233â†’
   234â†’            const results = this.generateResults();
   235â†’            this.displayResults(results);
   236â†’            
   237â†’            const winAmount = this.calculateWin(results);
   238â†’            
   239â†’            if (winAmount > 0) {
   240â†’                gameState.balance += winAmount;
   241â†’                gameState.totalWon += winAmount;
   242â†’                
   243â†’                if (winAmount > gameState.biggestWin) {
   244â†’                    gameState.biggestWin = winAmount;
   245â†’                }
   246â†’                
   247â†’                // Efectos especiales para micro-wins
   248â†’                const winUsdValue = winAmount * gameState.usdRate;
   249â†’                if (winUsdValue < 0.01) {
   250â†’                    this.showMicroWin(winAmount);
   251â†’                    this.triggerSpecialMicroWinEffects(winAmount);
   252â†’                } else {
   253â†’                    this.showMessage('ğŸ‰ Â¡GANASTE ' + NumberFormatter.formatCombined(winAmount, gameState.usdRate) + '!', 'win');
   254â†’                }
   255â†’                
   256â†’                this.highlightWinningSymbols();
   257â†’                
   258â†’                console.log('ğŸ† Ganancia: ' + NumberFormatter.formatCombined(winAmount, gameState.usdRate));
   259â†’            } else {
   260â†’                // Mensaje especial para apuestas ultra-accesibles sin win
   261â†’                const betUsdValue = totalBet * gameState.usdRate;
   262â†’                if (betUsdValue < 0.01) {
   263â†’                    const microMessages = [
   264â†’                        'ğŸ˜Š Â¡Solo era menos de un centavo!',
   265â†’                        'ğŸ’« Â¡PrÃ³ximo giro por centavos!',
   266â†’                        'ğŸª™ Â¡Ultra-accessible try again!',
   267â†’                        'âš¡ Â¡Casi gratis, intÃ©ntalo otra vez!'
   268â†’                    ];
   269â†’                    const randomMessage = microMessages[Math.floor(Math.random() * microMessages.length)];
   270â†’                    this.showMessage(randomMessage, 'neutral');
   271â†’                } else {
   272â†’                    this.showMessage('ğŸ˜” Sin suerte esta vez...', 'lose');
   273â†’                }
   274â†’                console.log('ğŸ˜” Sin ganancia');
   275â†’            }
   276â†’
   277â†’            gameState.isSpinning = false;
   278â†’            this.updateUI();
   279â†’            
   280â†’        }, 2000);
   281â†’    },
   282â†’
   283â†’    // Generar resultados aleatorios
   284â†’    generateResults() {
   285â†’        const results = [];
   286â†’        for (let i = 0; i < 5; i++) {
   287â†’            const reel = [];
   288â†’            for (let j = 0; j < 3; j++) {
   289â†’                reel.push(SYMBOLS[Math.floor(Math.random() * SYMBOLS.length)]);
   290â†’            }
   291â†’            results.push(reel);
   292â†’        }
   293â†’        console.log('ğŸ² Resultados generados:', results);
   294â†’        return results;
   295â†’    },
   296â†’
   297â†’    // Mostrar resultados
   298â†’    displayResults(results) {
   299â†’        for (let i = 0; i < 5; i++) {
   300â†’            const reel = document.getElementById('reel-' + i);
   301â†’            if (!reel) continue;
   302â†’            
   303â†’            const symbols = reel.querySelectorAll('.symbol');
   304â†’            
   305â†’            for (let j = 0; j < 3; j++) {
   306â†’                if (symbols[j] && results[i][j]) {
   307â†’                    const symbolData = results[i][j];
   308â†’                    
   309â†’                    // Limpiar contenido anterior
   310â†’                    symbols[j].innerHTML = '';
   311â†’                    symbols[j].classList.remove('winning');
   312â†’                    
   313â†’                    // Crear nueva imagen
   314â†’                    const img = document.createElement('img');
   315â†’                    img.src = symbolData.image;
   316â†’                    img.alt = symbolData.name;
   317â†’                    img.className = 'symbol-image';
   318â†’                    img.setAttribute('data-symbol-id', symbolData.id);
   319â†’                    
   320â†’                    // Fallback a emoji si la imagen no carga
   321â†’                    img.onerror = function() {
   322â†’                        symbols[j].innerHTML = symbolData.emoji;
   323â†’                        symbols[j].classList.add('symbol-emoji');
   324â†’                    };
   325â†’                    
   326â†’                    symbols[j].appendChild(img);
   327â†’                }
   328â†’            }
   329â†’        }
   330â†’    },
   331â†’
   332â†’    // Calcular ganancia simple
   333â†’    calculateWin(results) {
   334â†’        const centerLine = [results[0][1], results[1][1], results[2][1], results[3][1], results[4][1]];
   335â†’        
   336â†’        let consecutiveCount = 1;
   337â†’        let symbol = centerLine[0];
   338â†’        
   339â†’        for (let i = 1; i < centerLine.length; i++) {
   340â†’            if (centerLine[i].id === symbol.id || symbol.id === 'yeti') {
   341â†’                consecutiveCount++;
   342â†’            } else {
   343â†’                break;
   344â†’            }
   345â†’        }
   346â†’
   347â†’        let multiplier = 0;
   348â†’        if (consecutiveCount >= 5) multiplier = 50;
   349â†’        else if (consecutiveCount >= 4) multiplier = 20;
   350â†’        else if (consecutiveCount >= 3) multiplier = 5;
   351â†’
   352â†’        // Multiplicadores especiales por tipo de sÃ­mbolo
   353â†’        if (symbol.id === 'yeti') multiplier *= 2;        // Wild
   354â†’        if (symbol.id === 'rocket') multiplier *= 1.5;    // High value
   355â†’        if (symbol.id === 'alien') multiplier *= 3;       // Bonus
   356â†’        if (symbol.id === 'ufo') multiplier *= 2.5;       // Special
   357â†’
   358â†’        return Math.floor(gameState.currentBet * multiplier);
   359â†’    },
   360â†’
   361â†’    // Resaltar sÃ­mbolos ganadores
   362â†’    highlightWinningSymbols() {
   363â†’        const reels = document.querySelectorAll('.reel');
   364â†’        reels.forEach(function(reel, index) {
   365â†’            const centerSymbol = reel.children[1];
   366â†’            if (centerSymbol) {
   367â†’                centerSymbol.classList.add('winning');
   368â†’            }
   369â†’        });
   370â†’
   371â†’        setTimeout(function() {
   372â†’            document.querySelectorAll('.symbol.winning').forEach(function(symbol) {
   373â†’                symbol.classList.remove('winning');
   374â†’            });
   375â†’        }, 3000);
   376â†’    },
   377â†’
   378â†’    // Enhanced showMessage for micro-interactions
   379â†’    showMicroWin(amount) {
   380â†’        const usdValue = amount * gameState.usdRate;
   381â†’        
   382â†’        if (usdValue < 0.01) {
   383â†’            // Special celebration for micro-wins
   384â†’            const messageEl = document.getElementById('message');
   385â†’            if (messageEl) {
   386â†’                messageEl.classList.add('micro-win-celebration', 'active');
   387â†’                setTimeout(() => {
   388â†’                    messageEl.classList.remove('active');
   389â†’                }, 600);
   390â†’            }
   391â†’            
   392â†’            // Show special micro-win message
   393â†’            const messages = [
   394â†’                'ğŸ‰ Â¡Micro-win! Â¡Cada centavo cuenta!',
   395â†’                'ğŸ’« Â¡Ganaste casi gratis!',
   396â†’                'âš¡ Â¡Ultra-accessible win!',
   397â†’                'ğŸª™ Â¡Penny power activated!'
   398â†’            ];
   399â†’            const randomMessage = messages[Math.floor(Math.random() * messages.length)];
   400â†’            this.showMessage(randomMessage + ' ' + NumberFormatter.formatCombined(amount, gameState.usdRate), 'win');
   401â†’        }
   402â†’    },
   403â†’
   404â†’    // Trigger special effects for ultra-small wins
   405â†’    triggerSpecialMicroWinEffects(winAmount) {
   406â†’        const usdValue = winAmount * gameState.usdRate;
   407â†’        
   408â†’        if (usdValue < 0.01) {
   409â†’            // Create floating penny animations
   410â†’            this.createFloatingPennies();
   411â†’            
   412â†’            // Highlight the spin button with special effect
   413â†’            const spinBtn = document.getElementById('spinBtn');
   414â†’            if (spinBtn) {
   415â†’                spinBtn.classList.add('almost-free-pulse');
   416â†’                setTimeout(() => {
   417â†’                    spinBtn.classList.remove('almost-free-pulse');
   418â†’                }, 3000);
   419â†’            }
   420â†’            
   421â†’            // Add special glow to reels container
   422â†’            const reelsContainer = document.getElementById('reelsContainer');
   423â†’            if (reelsContainer) {
   424â†’                reelsContainer.style.boxShadow = '0 0 30px rgba(255, 230, 109, 0.8)';
   425â†’                setTimeout(() => {
   426â†’                    reelsContainer.style.boxShadow = '';
   427â†’                }, 2000);
   428â†’            }
   429â†’            
   430â†’            // Trigger confetti effect for really small wins
   431â†’            if (usdValue < 0.001) {
   432â†’                this.createMicroConfetti();
   433â†’            }
   434â†’        }
   435â†’    },
   436â†’
   437â†’    // Create floating penny animations
   438â†’    createFloatingPennies() {
   439â†’        const container = document.querySelector('.game-container') || document.body;
   440â†’        
   441â†’        for (let i = 0; i < 5; i++) {
   442â†’            const penny = document.createElement('div');
   443â†’            penny.textContent = 'Â¢';
   444â†’            penny.style.cssText = `
   445â†’                position: absolute;
   446â†’                font-size: 2rem;
   447â†’                color: var(--accent);
   448â†’                pointer-events: none;
   449â†’                z-index: 1000;
   450â†’                left: ${Math.random() * 100}%;
   451â†’                top: 100%;
   452â†’                animation: floatUp 3s ease-out forwards;
   453â†’            `;
   454â†’            
   455â†’            // Add floating animation
   456â†’            const floatKeyframes = `
   457â†’                @keyframes floatUp {
   458â†’                    0% {
   459â†’                        transform: translateY(0) rotate(0deg);
   460â†’                        opacity: 1;
   461â†’                    }
   462â†’                    100% {
   463â†’                        transform: translateY(-200px) rotate(360deg);
   464â†’                        opacity: 0;
   465â†’                    }
   466â†’                }
   467â†’            `;
   468â†’            
   469â†’            // Add keyframes to head if not already added
   470â†’            if (!document.getElementById('floatUpKeyframes')) {
   471â†’                const style = document.createElement('style');
   472â†’                style.id = 'floatUpKeyframes';
   473â†’                style.textContent = floatKeyframes;
   474â†’                document.head.appendChild(style);
   475â†’            }
   476â†’            
   477â†’            container.appendChild(penny);
   478â†’            
   479â†’            // Remove after animation
   480â†’            setTimeout(() => {
   481â†’                if (penny.parentNode) {
   482â†’                    penny.parentNode.removeChild(penny);
   483â†’                }
   484â†’            }, 3000);
   485â†’        }
   486â†’    },
   487â†’
   488â†’    // Create micro confetti for ultra-small wins
   489â†’    createMicroConfetti() {
   490â†’        const container = document.querySelector('.game-container') || document.body;
   491â†’        const colors = ['#FF6B35', '#4ECDC4', '#FFE66D', '#95E1D3', '#F38BA8'];
   492â†’        
   493â†’        for (let i = 0; i < 15; i++) {
   494â†’            const confetti = document.createElement('div');
   495â†’            confetti.style.cssText = `
   496â†’                position: absolute;
   497â†’                width: 8px;
   498â†’                height: 8px;
   499â†’                background: ${colors[Math.floor(Math.random() * colors.length)]};
