   580â†’// Ajustar apuesta - Ultra-Accessible Limits
   581â†’function adjustBet(change) {
   582â†’    let newBet;
   583â†’    
   584â†’    if (change === 'min') {
   585â†’        newBet = gameState.minBet;
   586â†’    } else if (change === 'max') {
   587â†’        newBet = gameState.maxBet;
   588â†’    } else if (change === 'next') {
   589â†’        // Find next higher popular bet
   590â†’        const currentIndex = gameState.popularBets.indexOf(gameState.currentBet);
   591â†’        if (currentIndex !== -1 && currentIndex < gameState.popularBets.length - 1) {
   592â†’            newBet = gameState.popularBets[currentIndex + 1];
   593â†’        } else {
   594â†’            newBet = gameState.popularBets[gameState.popularBets.length - 1];
   595â†’        }
   596â†’    } else if (change === 'prev') {
   597â†’        // Find next lower popular bet
   598â†’        const currentIndex = gameState.popularBets.indexOf(gameState.currentBet);
   599â†’        if (currentIndex > 0) {
   600â†’            newBet = gameState.popularBets[currentIndex - 1];
   601â†’        } else {
   602â†’            newBet = gameState.popularBets[0];
   603â†’        }
   604â†’    } else {
   605â†’        // Traditional numeric change
   606â†’        newBet = gameState.currentBet + change;
   607â†’    }
   608â†’    
   609â†’    // Validate against ultra-accessible limits
   610â†’    if (newBet >= gameState.minBet && newBet <= gameState.maxBet) {
   611â†’        const totalBet = newBet * gameState.activeLines;
   612â†’        if (totalBet <= gameState.maxTotalBet) {
   613â†’            gameState.currentBet = newBet;
   614â†’            updateUI();
   615â†’            console.log('ğŸ’° Apuesta ajustada a: ' + NumberFormatter.formatCombined(gameState.currentBet, gameState.usdRate));
   616â†’        } else {
   617â†’            showMessage(`âš ï¸ Apuesta total mÃ¡xima: ${NumberFormatter.formatCombined(gameState.maxTotalBet, gameState.usdRate)}`, 'lose');
   618â†’        }
   619â†’    } else {
   620â†’        showMessage(`âš ï¸ LÃ­mites: ${NumberFormatter.formatCombined(gameState.minBet, gameState.usdRate)} - ${NumberFormatter.formatCombined(gameState.maxBet, gameState.usdRate)}`, 'lose');
   621â†’    }
   622â†’}
