// Verificar wallet con backend
async function verifyWalletWithBackend(connectionData) {
    try {
        console.log('ğŸ”„ Verificando wallet con backend...');
        console.log('ğŸ“¤ Datos de conexiÃ³n:', connectionData);

        showMessage('ğŸ” Verificando firma con servidor...', 'neutral');

        const payload = {
            address: connectionData.address,
            signature: connectionData.signature.signature,
            message: connectionData.signature.message,
            walletType: connectionData.walletType,
            timestamp: connectionData.signature.timestamp
        };

        console.log('ğŸ“¤ Payload enviado:', payload);

        const response = await fetch('/api/wallet/connect', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(payload)
        });

        console.log('ğŸ“¥ Response status:', response.status);

        const result = await response.json();
        console.log('ğŸ“¥ Response data:', result);

        if (!response.ok) {
            throw new Error(result.error?.message || result.error || `HTTP ${response.status}`);
        }

        return result;
    } catch (error) {
        console.error('âŒ Error verificando con backend:', error);

        // Proporcionar errores mÃ¡s especÃ­ficos
        let errorMessage = error.message;
        if (error.message.includes('firma invÃ¡lida')) {
            errorMessage = 'La firma no es vÃ¡lida. Intenta conectar de nuevo.';
        } else if (error.message.includes('expirado')) {
            errorMessage = 'El mensaje de firma expirÃ³. Intenta de nuevo.';
        } else if (error.message.includes('network')) {
            errorMessage = 'Error de conexiÃ³n con el servidor. Verifica tu internet.';
        }

        return { success: false, error: errorMessage };
    }
}

