// Create micro confetti for ultra-small wins
function createMicroConfetti() {
    const container = document.querySelector('.game-container') || document.body;
    const colors = ['#FF6B35', '#4ECDC4', '#FFE66D', '#95E1D3', '#F38BA8'];

    for (let i = 0; i < 15; i++) {
