        // Add confetti animation
        const confettiKeyframes = `
            @keyframes confettiFall {
                0% {
                    transform: translateY(0) rotate(0deg) scale(1);
                    opacity: 1;
                }
                100% {
                    transform: translateY(200px) rotate(720deg) scale(0);
                    opacity: 0;
                }
            }
