    setBet(amount) {
        if (amount >= gameState.minBet && amount <= gameState.maxBet) {
            const totalBet = amount * gameState.activeLines;
            if (totalBet <= gameState.maxTotalBet) {
                gameState.currentBet = amount;

                // Trigger micro-interactions for ultra-accessible amounts
                const usdValue = amount * gameState.usdRate;
                if (usdValue < 0.01) {
                    this.triggerMicroBetAnimation(amount);
                    this.showMessage(`ğŸ‰ Â¡Apuesta ultra-accesible! ${NumberFormatter.formatCombined(amount, gameState.usdRate)}`, 'win');
                } else {
                    this.showMessage(`ğŸ’° Apuesta establecida: ${NumberFormatter.formatCombined(amount, gameState.usdRate)}`, 'neutral');
                }

                this.updateUI();
                console.log('ğŸ’° Apuesta establecida a: ' + NumberFormatter.formatCombined(gameState.currentBet, gameState.usdRate));
            } else {
                this.showMessage(`âš ï¸ Apuesta total mÃ¡xima: ${NumberFormatter.formatCombined(gameState.maxTotalBet, gameState.usdRate)}`, 'lose');
            }
        }
    },

    // Ajustar lÃ­neas
    adjustLines(change) {
        const newLines = gameState.activeLines + change;
