     4â†’// Ultra-Accessible Number Formatting Utilities with Performance Cache
     5â†’const NumberFormatter = {
     6â†’    // Cache for performance optimization
     7â†’    _tokenFormatCache: new Map(),
     8â†’    _usdFormatCache: new Map(),
     9â†’    
    10â†’    // Format token amount for display (with cache)
    11â†’    formatTokenAmount: (amount) => {
    12â†’        // Check cache first
    13â†’        if (NumberFormatter._tokenFormatCache.has(amount)) {
    14â†’            return NumberFormatter._tokenFormatCache.get(amount);
    15â†’        }
    16â†’        
    17â†’        let result;
    18â†’        if (amount >= 1000000000) {
    19â†’            result = (amount / 1000000000).toFixed(1) + 'B MY';
    20â†’        } else if (amount >= 1000000) {
    21â†’            // Handle edge case: 999,500,000 should be "999.5M MY" not "1000M MY"
    22â†’            const millions = amount / 1000000;
    23â†’            if (millions >= 999.5) {
    24â†’                result = (amount / 1000000000).toFixed(2) + 'B MY';
    25â†’            } else {
    26â†’                result = millions.toFixed(1) + 'M MY';
    27â†’            }
    28â†’        } else if (amount >= 1000) {
    29â†’            // Handle edge case: 999,500 should be "999K MY" not "1000K MY"
    30â†’            const thousands = amount / 1000;
    31â†’            if (thousands >= 999.5) {
    32â†’                result = (amount / 1000000).toFixed(2) + 'M MY';
    33â†’            } else {
    34â†’                result = thousands.toFixed(0) + 'K MY';
    35â†’            }
    36â†’        } else {
    37â†’            result = amount.toLocaleString() + ' MY';
    38â†’        }
    39â†’        
    40â†’        // Cache result (limit cache size to prevent memory issues)
    41â†’        if (NumberFormatter._tokenFormatCache.size < 1000) {
    42â†’            NumberFormatter._tokenFormatCache.set(amount, result);
    43â†’        }
    44â†’        
    45â†’        return result;
    46â†’    },
    47â†’
    48â†’    // Format USD equivalent (optimized for ultra-micro amounts with cache)
    49â†’    formatUSDEquivalent: (tokenAmount, rate = 0.0000001037) => {
    50â†’        // Create cache key
    51â†’        const cacheKey = `${tokenAmount}_${rate}`;
    52â†’        
    53â†’        // Check cache first
    54â†’        if (NumberFormatter._usdFormatCache.has(cacheKey)) {
    55â†’            return NumberFormatter._usdFormatCache.get(cacheKey);
    56â†’        }
    57â†’        
    58â†’        const usdValue = tokenAmount * rate;
    59â†’        let result;
    60â†’        
    61â†’        if (usdValue < 0.0001) {
    62â†’            // Ultra-micro amounts: show as fractions of cents (â€° = per-mille = 1/1000)
    63â†’            result = ` (~${(usdValue * 10000).toFixed(1)}â€°)`;
    64â†’        } else if (usdValue < 0.001) {
    65â†’            // Very small amounts: show as 10ths of cents
    66â†’            result = ` (~${(usdValue * 1000).toFixed(1)}â‚â‚€Â¢)`;
    67â†’        } else if (usdValue < 0.01) {
    68â†’            // Small amounts: show as cents with 1 decimal
    69â†’            result = ` (~${(usdValue * 100).toFixed(1)}Â¢)`;
    70â†’        } else if (usdValue < 1) {
    71â†’            // Sub-dollar amounts: show as cents
    72â†’            result = ` (~${(usdValue * 100).toFixed(0)}Â¢)`;
    73â†’        } else {
    74â†’            result = ` (~$${usdValue.toFixed(2)})`;
    75â†’        }
    76â†’        
    77â†’        // Cache result (limit cache size to prevent memory issues)
    78â†’        if (NumberFormatter._usdFormatCache.size < 1000) {
    79â†’            NumberFormatter._usdFormatCache.set(cacheKey, result);
    80â†’        }
    81â†’        
    82â†’        return result;
    83â†’    },
    84â†’
    85â†’    // Clear cache periodically for memory management
    86â†’    clearCache: () => {
    87â†’        NumberFormatter._tokenFormatCache.clear();
    88â†’        NumberFormatter._usdFormatCache.clear();
    89â†’    },
    90â†’
    91â†’    // Cache statistics for debugging
    92â†’    getCacheStats: () => {
    93â†’        return {
    94â†’            tokenCacheSize: NumberFormatter._tokenFormatCache.size,
    95â†’            usdCacheSize: NumberFormatter._usdFormatCache.size
    96â†’        };
    97â†’    },
    98â†’
    99â†’    // Format combined display (token + USD)
   100â†’    formatCombined: (tokenAmount, rate = 0.0000001037) => {
   101â†’        return NumberFormatter.formatTokenAmount(tokenAmount) + NumberFormatter.formatUSDEquivalent(tokenAmount, rate);
   102â†’    }
