// MoonYetis Slots - Game Logic
console.log('ğŸš€ MoonYetis Slots Script Loading...');

// Ultra-Accessible Number Formatting Utilities with Performance Cache
const NumberFormatter = {
    // Cache for performance optimization
    _tokenFormatCache: new Map(),
    _usdFormatCache: new Map(),

    // Format token amount for display (with cache)
    formatTokenAmount: (amount) => {
        // Check cache first
        if (NumberFormatter._tokenFormatCache.has(amount)) {
            return NumberFormatter._tokenFormatCache.get(amount);
        }

        let result;
        if (amount >= 1000000000) {
            result = (amount / 1000000000).toFixed(1) + 'B MY';
        } else if (amount >= 1000000) {
            // Handle edge case: 999,500,000 should be "999.5M MY" not "1000M MY"
            const millions = amount / 1000000;
            if (millions >= 999.5) {
                result = (amount / 1000000000).toFixed(2) + 'B MY';
            } else {
                result = millions.toFixed(1) + 'M MY';
            }
        } else if (amount >= 1000) {
            // Handle edge case: 999,500 should be "999K MY" not "1000K MY"
            const thousands = amount / 1000;
            if (thousands >= 999.5) {
                result = (amount / 1000000).toFixed(2) + 'M MY';
            } else {
                result = thousands.toFixed(0) + 'K MY';
            }
        } else {
            result = amount.toLocaleString() + ' MY';
        }

        // Cache result (limit cache size to prevent memory issues)
        if (NumberFormatter._tokenFormatCache.size < 1000) {
            NumberFormatter._tokenFormatCache.set(amount, result);
        }

        return result;
    },

    // Format USD equivalent (optimized for ultra-micro amounts with cache)
    formatUSDEquivalent: (tokenAmount, rate = 0.0000001037) => {
        // Create cache key
        const cacheKey = `${tokenAmount}_${rate}`;

        // Check cache first
        if (NumberFormatter._usdFormatCache.has(cacheKey)) {
            return NumberFormatter._usdFormatCache.get(cacheKey);
        }

        const usdValue = tokenAmount * rate;
        let result;

        if (usdValue < 0.0001) {
            // Ultra-micro amounts: show as fractions of cents (â€° = per-mille = 1/1000)
            result = ` (~${(usdValue * 10000).toFixed(1)}â€°)`;
        } else if (usdValue < 0.001) {
            // Very small amounts: show as 10ths of cents
            result = ` (~${(usdValue * 1000).toFixed(1)}â‚â‚€Â¢)`;
        } else if (usdValue < 0.01) {
            // Small amounts: show as cents with 1 decimal
            result = ` (~${(usdValue * 100).toFixed(1)}Â¢)`;
        } else if (usdValue < 1) {
            // Sub-dollar amounts: show as cents
            result = ` (~${(usdValue * 100).toFixed(0)}Â¢)`;
        } else {
            result = ` (~$${usdValue.toFixed(2)})`;
        }

        // Cache result (limit cache size to prevent memory issues)
        if (NumberFormatter._usdFormatCache.size < 1000) {
            NumberFormatter._usdFormatCache.set(cacheKey, result);
        }

        return result;
    },

    // Clear cache periodically for memory management
    clearCache: () => {
        NumberFormatter._tokenFormatCache.clear();
        NumberFormatter._usdFormatCache.clear();
    },

    // Cache statistics for debugging
    getCacheStats: () => {
        return {
            tokenCacheSize: NumberFormatter._tokenFormatCache.size,
            usdCacheSize: NumberFormatter._usdFormatCache.size
        };
    },

    // Format combined display (token + USD)
    formatCombined: (tokenAmount, rate = 0.0000001037) => {
        return NumberFormatter.formatTokenAmount(tokenAmount) + NumberFormatter.formatUSDEquivalent(tokenAmount, rate);
    }
};

// ConfiguraciÃ³n del juego - usando imÃ¡genes reales
const SYMBOLS = [
    { id: 'yeti', name: 'Yeti Wild', image: 'assets/symbols/yeti-wild.png', emoji: 'ğŸ”ï¸' },
    { id: 'rocket', name: 'Rocket High', image: 'assets/symbols/rocket-high.png', emoji: 'ğŸš€' },
    { id: 'moon', name: 'Moon Scatter', image: 'assets/symbols/moon-scatter.png', emoji: 'ğŸŒ™' },
    { id: 'coin', name: 'Coin Medium', image: 'assets/symbols/coin-medium.png', emoji: 'ğŸª™' },
    { id: 'star', name: 'Star Medium', image: 'assets/symbols/star-medium.png', emoji: 'â­' },
    { id: 'planet', name: 'Planet Low', image: 'assets/symbols/planet-low.png', emoji: 'ğŸª' },
    { id: 'alien', name: 'Alien Bonus', image: 'assets/symbols/alien-bonus.png', emoji: 'ğŸ‘½' },
    { id: 'ufo', name: 'UFO Special', image: 'assets/symbols/ufo-special.png', emoji: 'ğŸ›¸' }
];

// Array de IDs para compatibilidad con cÃ³digo existente
const SYMBOL_IDS = SYMBOLS.map(s => s.id);

// Estado del juego - Ultra-Accessible Limits
let gameState = {
    balance: 0,
    currentBet: 100000, // Default bet 100K MOONYETIS (~$0.01 current, $1 if 100x)
    activeLines: 25,
    isSpinning: false,
    connectedWallet: false,
    totalSpins: 0,
    totalWagered: 0,
    totalWon: 0,
    biggestWin: 0,
    // Ultra-accessible betting limits
    minBet: 10000,        // 10K MOONYETIS (~$0.001 current)
    maxBet: 50000000,     // 50M MOONYETIS (~$5.18 current)
    maxTotalBet: 250000000, // 250M MOONYETIS total (~$25.9 current)
    popularBets: [10000, 50000, 100000, 500000, 1000000, 5000000], // Ultra-scalable range
    usdRate: 0.0000001037 // Current USD rate per MOONYETIS
};

// Inicializar juego
function initGame() {
    console.log('ğŸ° Inicializando MoonYetis Slots...');
    createReels();
    updateUI();
    console.log('âœ… Juego inicializado correctamente');
}

// Crear rodillos
function createReels() {
    const container = document.getElementById('reelsContainer');
    if (!container) {
        console.error('âŒ No se encontrÃ³ el contenedor de rodillos');
        return;
    }

    container.innerHTML = '';

    for (let i = 0; i < 5; i++) {
        const reel = document.createElement('div');
        reel.className = 'reel';
        reel.id = `reel-${i}`;

        for (let j = 0; j < 3; j++) {
            const symbol = document.createElement('div');
            symbol.className = 'symbol';

            // Seleccionar sÃ­mbolo aleatorio
            const randomSymbol = SYMBOLS[Math.floor(Math.random() * SYMBOLS.length)];

            // Crear imagen
            const img = document.createElement('img');
            img.src = randomSymbol.image;
            img.alt = randomSymbol.name;
            img.className = 'symbol-image';
            img.setAttribute('data-symbol-id', randomSymbol.id);

            // Fallback a emoji si la imagen no carga
            img.onerror = function() {
                symbol.innerHTML = randomSymbol.emoji;
                symbol.classList.add('symbol-emoji');
            };

            symbol.appendChild(img);
            reel.appendChild(symbol);
        }

        container.appendChild(reel);
    }
    console.log('ğŸ² Rodillos creados correctamente');
}

// Conectar wallet REAL
async function connectWallet() {
    console.log('ğŸ”— Iniciando conexiÃ³n real de wallet...');

    try {
        // Detectar wallets disponibles
        const availableWallets = await walletManager.detectAvailableWallets();

        if (!availableWallets.unisat.available && !availableWallets.okx.available) {
            showWalletInstallationMessage(availableWallets);
            return;
        }

        // Mostrar selector de wallet
        const walletChoice = await showWalletSelector(availableWallets);
        if (!walletChoice) {
            showMessage('âš ï¸ ConexiÃ³n cancelada', 'lose');
            return;
        }

        // Mostrar loading
        showMessage('ğŸ”„ Conectando wallet...', 'neutral');

        // Conectar wallet seleccionada
        let connectionResult;
        if (walletChoice === 'unisat') {
            showMessage('ğŸ¦„ Conectando UniSat... Por favor autoriza en la extensiÃ³n', 'neutral');
            connectionResult = await walletManager.connectUniSat();
        } else if (walletChoice === 'okx') {
            showMessage('ğŸŸ  Conectando OKX... Por favor autoriza en la extensiÃ³n', 'neutral');
            connectionResult = await walletManager.connectOKX();
        }

        if (connectionResult.success) {
            // Verificar conexiÃ³n con backend
            const backendVerification = await verifyWalletWithBackend(connectionResult);

            if (backendVerification.success) {
                // Actualizar estado del juego
                gameState.connectedWallet = true;
                gameState.walletAddress = connectionResult.address;
                gameState.walletType = connectionResult.walletType;
                gameState.balance = connectionResult.balance.total;

                // Actualizar UI
                updateWalletUI(connectionResult);
                updateUI();

                // Setup listeners para cambios de wallet
                setupWalletEventListeners();

                // Registrar para monitoreo de depÃ³sitos
                await registerForDepositMonitoring();

                showMessage(`ğŸ‰ Wallet ${connectionResult.walletType.toUpperCase()} conectada!`, 'win');
                console.log('âœ… Wallet conectada exitosamente:', connectionResult.address);
            } else {
                showMessage(`âŒ Error verificando wallet: ${backendVerification.error}`, 'lose');
            }
        } else {
            showMessage(`âŒ Error conectando: ${connectionResult.error}`, 'lose');
        }

    } catch (error) {
        console.error('âŒ Error en conexiÃ³n de wallet:', error);
        showMessage(`âŒ Error inesperado: ${error.message}`, 'lose');
    }
}

// Mostrar selector de wallet
async function showWalletSelector(availableWallets) {
    console.log('ğŸ”— Mostrando selector de wallet:', availableWallets);

    return new Promise((resolve) => {
        const modal = document.createElement('div');
        modal.className = 'wallet-selector-modal';
        modal.innerHTML = `
            <div class="wallet-selector-content">
                <h3>ğŸ”— Conecta tu Wallet</h3>
                <p>Selecciona tu wallet preferida para jugar MoonYetis Slots</p>
                <div class="wallet-buttons">
                    ${availableWallets.unisat.available ? 
                        `<button class="wallet-btn" data-wallet="unisat">
                            ${availableWallets.unisat.icon} ${availableWallets.unisat.name}
                            <span class="wallet-status">âœ… Detectada</span>
                        </button>` : 
                        `<button class="wallet-btn disabled" data-wallet="unisat" data-action="install">
                            ${availableWallets.unisat.icon} ${availableWallets.unisat.name}
                            <span class="wallet-status">âŒ No instalada</span>
                        </button>`
                    }
                    ${availableWallets.okx.available ? 
                        `<button class="wallet-btn" data-wallet="okx">
                            ${availableWallets.okx.icon} ${availableWallets.okx.name}
                            <span class="wallet-status">âœ… Detectada</span>
                        </button>` : 
                        `<button class="wallet-btn disabled" data-wallet="okx" data-action="install">
                            ${availableWallets.okx.icon} ${availableWallets.okx.name}
                            <span class="wallet-status">âŒ No instalada</span>
                        </button>`
                    }
                </div>
                <button class="cancel-btn" data-action="cancel">Cancelar</button>
                <div style="margin-top: 1rem; font-size: 0.8rem; color: #666;">
                    <p>ğŸ” Debug: UniSat=${availableWallets.unisat.available}, OKX=${availableWallets.okx.available}</p>
                </div>
            </div>
        `;

        // Usar addEventListener en lugar de onclick
        modal.addEventListener('click', (event) => {
            const button = event.target.closest('button');
            if (!button) return;

            const walletType = button.dataset.wallet;
            const action = button.dataset.action;

            console.log('ğŸ‘† Click detectado:', { walletType, action, button });

            if (action === 'install') {
                const urls = {
                    unisat: 'https://unisat.io/',
                    okx: 'https://okx.com/web3'
                };
                console.log('ğŸ”— Abriendo URL de instalaciÃ³n:', urls[walletType]);
                window.open(urls[walletType], '_blank');
                return;
            }

            if (action === 'cancel') {
                console.log('âŒ Cancelando selecciÃ³n de wallet');
                document.body.removeChild(modal);
                resolve(null);
                return;
            }

            if (walletType && !button.classList.contains('disabled')) {
                console.log('âœ… Wallet seleccionada:', walletType);
                document.body.removeChild(modal);
                resolve(walletType);
            }
        });

        // Cleanup de funciones globales anteriores
        delete window.selectWallet;
        delete window.installWallet;
        delete window.cancelWalletSelection;

        document.body.appendChild(modal);
        console.log('ğŸ“‹ Modal de wallet agregado al DOM');
    });
}

// Verificar wallet con backend
async function verifyWalletWithBackend(connectionData) {
    try {
        console.log('ğŸ”„ Verificando wallet con backend...');
        console.log('ğŸ“¤ Datos de conexiÃ³n:', connectionData);

        showMessage('ğŸ” Verificando firma con servidor...', 'neutral');

        const payload = {
            address: connectionData.address,
            signature: connectionData.signature.signature,
            message: connectionData.signature.message,
            walletType: connectionData.walletType,
            timestamp: connectionData.signature.timestamp
        };

        console.log('ğŸ“¤ Payload enviado:', payload);

        const response = await fetch('/api/wallet/connect', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(payload)
        });

        console.log('ğŸ“¥ Response status:', response.status);

        const result = await response.json();
        console.log('ğŸ“¥ Response data:', result);

        if (!response.ok) {
            throw new Error(result.error?.message || result.error || `HTTP ${response.status}`);
        }

        return result;
    } catch (error) {
        console.error('âŒ Error verificando con backend:', error);

        // Proporcionar errores mÃ¡s especÃ­ficos
        let errorMessage = error.message;
        if (error.message.includes('firma invÃ¡lida')) {
            errorMessage = 'La firma no es vÃ¡lida. Intenta conectar de nuevo.';
        } else if (error.message.includes('expirado')) {
            errorMessage = 'El mensaje de firma expirÃ³. Intenta de nuevo.';
        } else if (error.message.includes('network')) {
            errorMessage = 'Error de conexiÃ³n con el servidor. Verifica tu internet.';
        }

        return { success: false, error: errorMessage };
    }
}

// Actualizar UI de wallet
function updateWalletUI(connectionData) {
    // Actualizar botÃ³n de wallet
    const walletBtn = document.getElementById('connectWallet');
    const walletBtnText = document.getElementById('walletBtnText');
    if (walletBtn && walletBtnText) {
        walletBtnText.textContent = `${connectionData.walletType === 'unisat' ? 'ğŸ¦„' : 'ğŸŸ '} ${connectionData.address.substring(0, 6)}...${connectionData.address.substring(-4)}`;
        walletBtn.onclick = disconnectWallet;
        walletBtn.title = 'Click to disconnect wallet';
    }

    // Actualizar status de conexiÃ³n
    const connectionStatus = document.getElementById('connectionStatus');
    const statusText = document.getElementById('statusText');
    if (connectionStatus && statusText) {
        connectionStatus.className = 'status-indicator connected';
        statusText.textContent = `Connected (${connectionData.walletType.toUpperCase()})`;
    }

    // Actualizar balance
    const balanceDisplay = document.getElementById('balanceDisplay');
    const balance = document.getElementById('balance');
    if (balanceDisplay && balance) {
        balanceDisplay.style.display = 'block';
        balance.textContent = connectionData.balance.total;
    }
}

// Setup event listeners para wallet
function setupWalletEventListeners() {
    walletManager.onAccountChanged = (newAccount) => {
        showMessage('ğŸ”„ Cuenta de wallet cambiada, reconectando...', 'neutral');
        setTimeout(() => {
            disconnectWallet();
        }, 2000);
    };

    walletManager.onNetworkChanged = (network) => {
        console.log('ğŸŒ Red cambiada:', network);
        showMessage('âš ï¸ Red de wallet cambiada', 'neutral');
    };
}

// Desconectar wallet
function disconnectWallet() {
    console.log('ğŸ”Œ Desconectando wallet...');

    walletManager.disconnect();

    gameState.connectedWallet = false;
    gameState.walletAddress = null;
    gameState.walletType = null;
    gameState.balance = 0;

    // Restaurar botÃ³n de wallet
    const walletBtn = document.getElementById('connectWallet');
    const walletBtnText = document.getElementById('walletBtnText');
    if (walletBtn && walletBtnText) {
        walletBtnText.textContent = 'Connect Wallet';
        walletBtn.onclick = connectWallet;
        walletBtn.title = 'Connect your wallet to start playing';
    }

    // Actualizar status de conexiÃ³n
    const connectionStatus = document.getElementById('connectionStatus');
    const statusText = document.getElementById('statusText');
    if (connectionStatus && statusText) {
        connectionStatus.className = 'status-indicator disconnected';
        statusText.textContent = 'Not Connected';
    }

    // Ocultar balance
    const balanceDisplay = document.getElementById('balanceDisplay');
    if (balanceDisplay) {
        balanceDisplay.style.display = 'none';
    }

    updateUI();
    showMessage('ğŸ”Œ Wallet desconectada', 'neutral');
}

// Mostrar mensaje de instalaciÃ³n de wallets
function showWalletInstallationMessage(availableWallets) {
    const message = `
        âš ï¸ No se encontraron wallets compatibles.

        Instala una de estas wallets:
        ${!availableWallets.unisat.installed ? 'â€¢ UniSat Wallet (https://unisat.io/)' : ''}
        ${!availableWallets.okx.installed ? 'â€¢ OKX Wallet (https://okx.com/web3)' : ''}
    `;

    showMessage(message, 'lose');
}

