    80‚Üí        directives: {
    81‚Üí            defaultSrc: ["'self'"],
    82‚Üí            scriptSrc: ["'self'", "'unsafe-inline'", "'unsafe-eval'", "https://cdnjs.cloudflare.com"],
    83‚Üí            scriptSrcAttr: ["'unsafe-inline'"], // Permitir event handlers inline
    84‚Üí            styleSrc: ["'self'", "'unsafe-inline'", "https://fonts.googleapis.com"],
    85‚Üí            fontSrc: ["'self'", "https://fonts.gstatic.com"],
    86‚Üí            imgSrc: ["'self'", "data:", "https:"],
    87‚Üí            connectSrc: ["'self'", "ws:", "wss:", "https:"]
    88‚Üí        }
    89‚Üí    }
    90‚Üí}));
