   105‚Üí// Initialize middleware after loading secure session config
   106‚Üíasync function initializeMiddleware() {
   107‚Üí    const sessionConfig = await initializeSessionConfig();
   108‚Üí    app.use(session(sessionConfig));
   109‚Üí    
   110‚Üí    // Add security headers
   111‚Üí    app.use((req, res, next) => {
   112‚Üí        const securityHeaders = sslManager.getSecurityHeaders();
   113‚Üí        Object.entries(securityHeaders).forEach(([header, value]) => {
   114‚Üí            res.setHeader(header, value);
   115‚Üí        });
   116‚Üí        next();
   117‚Üí    });
   118‚Üí    
   119‚Üí    // Phase 2: Advanced rate limiting and DDoS protection
   120‚Üí    app.use(rateLimiterService.ddosProtection());
   121‚Üí    app.use('/api', rateLimiterService.generalLimiter());
   122‚Üí    app.use('/api/blockchain', rateLimiterService.walletLimiter());
   123‚Üí    app.use('/api/blockchain/deposit', rateLimiterService.transactionLimiter());
   124‚Üí    app.use('/api/blockchain/withdraw', rateLimiterService.transactionLimiter());
   125‚Üí    app.use('/api/game', rateLimiterService.gameLimiter());
   126‚Üí    app.use('/api/monitoring', rateLimiterService.strictLimiter());
   127‚Üí    
   128‚Üí    console.log('üîí Security middleware initialized');
   129‚Üí    console.log('üõ°Ô∏è Advanced rate limiting and DDoS protection enabled');
   130‚Üí}
   131‚Üí
   132‚Üí// Legacy rate limiting (keeping for backwards compatibility)
   133‚Üíconst limiter = rateLimit({
   134‚Üí    windowMs: 15 * 60 * 1000,
   135‚Üí    max: 100,
   136‚Üí    message: { error: 'Too many requests' }
   137‚Üí});
   138‚Üíapp.use('/api/', limiter);
   139‚Üí
   140‚Üí// Serve frontend
   141‚Üíapp.use(express.static(path.join(__dirname, 'frontend')));
   142‚Üí
   143‚Üí// Enhanced WebSocket handling for blockchain features
   144‚Üífunction initializeWebSocket(server) {
   145‚Üí    const wss = new WebSocket.Server({ server });
   146‚Üí    
   147‚Üí    wss.on('connection', (ws) => {
   148‚Üí        console.log('üîó New WebSocket connection');
   149‚Üí        let walletAddress = null;
   150‚Üí    
   151‚Üí    ws.on('message', (message) => {
   152‚Üí        try {
   153‚Üí            const data = JSON.parse(message);
   154‚Üí            
   155‚Üí            switch (data.type) {
   156‚Üí                case 'wallet_connect':
   157‚Üí                    walletAddress = data.walletAddress;
   158‚Üí                    ws.send(JSON.stringify({ 
   159‚Üí                        type: 'wallet_connected', 
   160‚Üí                        walletAddress 
   161‚Üí                    }));
   162‚Üí                    break;
   163‚Üí                    
   164‚Üí                case 'game_update':
   165‚Üí                    // Broadcast game updates to connected clients
   166‚Üí                    wss.clients.forEach(client => {
   167‚Üí                        if (client !== ws && client.readyState === WebSocket.OPEN) {
   168‚Üí                            client.send(JSON.stringify({
   169‚Üí                                type: 'game_broadcast',
   170‚Üí                                data: data.gameData
   171‚Üí                            }));
   172‚Üí                        }
   173‚Üí                    });
   174‚Üí                    break;
   175‚Üí                    
   176‚Üí                case 'ping':
   177‚Üí                    ws.send(JSON.stringify({ type: 'pong', timestamp: Date.now() }));
   178‚Üí                    break;
   179‚Üí                    
   180‚Üí                default:
   181‚Üí                    ws.send(JSON.stringify({ type: 'pong', data }));
   182‚Üí            }
   183‚Üí        } catch (error) {
   184‚Üí            console.error('WebSocket error:', error);
   185‚Üí            ws.send(JSON.stringify({ 
   186‚Üí                type: 'error', 
   187‚Üí                message: 'Invalid message format' 
   188‚Üí            }));
   189‚Üí        }
   190‚Üí    });
   191‚Üí    
   192‚Üí    ws.on('close', () => {
   193‚Üí        console.log('üîå WebSocket disconnected', walletAddress ? `(${walletAddress})` : '');
   194‚Üí    });
   195‚Üí    
   196‚Üí    // Send welcome message with blockchain capabilities
   197‚Üí    ws.send(JSON.stringify({
   198‚Üí        type: 'welcome',
   199‚Üí        features: {
   200‚Üí            blockchain: true,
   201‚Üí            realGambling: true,
   202‚Üí            provablyFair: true,
   203‚Üí            walletSupport: ['UniSat', 'OKX']
   204‚Üí        }
   205‚Üí    }));
   206‚Üí    });
   207‚Üí
   208‚Üí    return wss;
   209‚Üí}
   210‚Üí
   211‚Üí// Database middleware - make db available to routes
   212‚Üíapp.use((req, res, next) => {
   213‚Üí    req.app.locals.db = database;
   214‚Üí    next();
   215‚Üí});
   216‚Üí
   217‚Üí// Import new wallet and transaction routes
   218‚Üíconst walletRoutes = require('./routes/wallet');
   219‚Üíconst transactionRoutes = require('./routes/transactions');
   220‚Üíconst transactionMonitor = require('./services/transactionMonitor');
   221‚Üíconst gameTransactionHandler = require('./services/gameTransactionHandler');
   222‚Üí
   223‚Üí// API Routes
   224‚Üíapp.use('/api/blockchain', blockchainRoutes);
   225‚Üíapp.use('/api/leaderboard', leaderboardRoutes);
