   254‚Üí            network: process.env.NODE_ENV === 'production' ? 'mainnet' : 'testnet'
   255‚Üí        },
   256‚Üí        uptime: process.uptime()
   257‚Üí    });
   258‚Üí});
   259‚Üí
   260‚Üíapp.get('/api/metrics', (req, res) => {
