   270‚Üí
   271‚Üí// Demo game endpoints
   272‚Üíapp.post('/api/game/spin', (req, res) => {
   273‚Üí    const { betAmount, activeLines } = req.body;
   274‚Üí    
   275‚Üí    // Simulate spin result
   276‚Üí    const reels = [];
   277‚Üí    const symbols = ['üèîÔ∏è', 'üöÄ', 'üåô', 'ü™ô', '‚≠ê', 'ü™ê', 'üëΩ', 'üõ∏'];
   278‚Üí    
   279‚Üí    for (let i = 0; i < 5; i++) {
   280‚Üí        const reel = [];
