   326‚Üí// Global error handler (debe ser el √∫ltimo middleware)
   327‚Üíapp.use(globalErrorHandler);
   328‚Üí
   329‚Üí// Handle uncaught exceptions
   330‚Üíprocess.on('uncaughtException', (err) => {
   331‚Üí    errorLogger.error('Uncaught Exception:', {
   332‚Üí        error: err.message,
   333‚Üí        stack: err.stack,
   334‚Üí        timestamp: new Date().toISOString()
   335‚Üí    });
