   349‚Üí    // Forzar salida despu√©s de 5 segundos
   350‚Üí    setTimeout(() => {
   351‚Üí        process.exit(1);
   352‚Üí    }, 5000);
   353‚Üí});
   354‚Üí
   355‚Üí// Handle unhandled promise rejections
   356‚Üíprocess.on('unhandledRejection', (reason, promise) => {
