   370‚Üí            process.exit(1);
   371‚Üí        });
   372‚Üí    } else {
   373‚Üí        process.exit(1);
   374‚Üí    }
   375‚Üí    
   376‚Üí    // Forzar salida despu√©s de 5 segundos
   377‚Üí    setTimeout(() => {
   378‚Üí        process.exit(1);
   379‚Üí    }, 5000);
   380‚Üí});
   381‚Üí
   382‚Üí// Enhanced graceful shutdown
   383‚Üíconst gracefulShutdown = async (signal) => {
   384‚Üí    console.log(`${signal} received, shutting down gracefully...`);
