   393‚Üí    });
   394‚Üí    
   395‚Üí    wss.close(() => {
   396‚Üí        console.log('WebSocket server closed');
   397‚Üí    });
   398‚Üí    
   399‚Üí    // Shutdown blockchain services
   400‚Üí    try {
   401‚Üí        console.log('üîó Shutting down blockchain services...');
