/**
 * M√≥dulo de gesti√≥n de wallets para Bitcoin
 * Maneja la detecci√≥n y conexi√≥n con wallets UniSat y OKX
 */

const Wallets = {
  // Estado actual de la wallet conectada
  _currentWallet: null,

  /**
   * Detecta las wallets disponibles en el navegador
   * @returns {Object} { success: boolean, data: Array, error?: string }
   */
  detectAvailableWallets() {
    console.log('üîç Detectando wallets disponibles...');

    const availableWallets = [];

    try {
      // Verificar UniSat
      if (typeof window !== 'undefined' && window.unisat) {
        console.log('‚úÖ UniSat detectada');
        availableWallets.push({
          name: 'UniSat',
          id: 'unisat',
          available: true,
          icon: 'unisat-icon'
        });
      } else {
        console.log('‚ùå UniSat no detectada');
      }

      // Verificar OKX
      if (typeof window !== 'undefined' && window.okxwallet && window.okxwallet.bitcoin) {
        console.log('‚úÖ OKX detectada');
        availableWallets.push({
          name: 'OKX',
          id: 'okx',
          available: true,
          icon: 'okx-icon'
        });
      } else {
        console.log('‚ùå OKX no detectada');
      }

      console.log(`üéØ Total de wallets detectadas: ${availableWallets.length}`);

      return {
        success: true,
        data: availableWallets
      };

    } catch (error) {
      console.error('‚ùå Error detectando wallets:', error);
      return {
        success: false,
        data: [],
        error: error.message
      };
    }
  },

  /**
   * Conecta con la wallet UniSat
   * @returns {Object} { success: boolean, data?: Object, error?: string }
   */
  async connectUniSat() {
    console.log('üîó Intentando conectar con UniSat...');

    try {
      // Verificar si UniSat est√° disponible
      if (typeof window === 'undefined' || !window.unisat) {
        // Simular conexi√≥n en entorno de desarrollo
        if (process.env.NODE_ENV === 'development' || typeof process === 'undefined') {
          console.log('‚ö†Ô∏è Simulando conexi√≥n UniSat en desarrollo');
          this._currentWallet = {
            name: 'UniSat (Simulado)',
            id: 'unisat',
            address: 'bc1qxy2kgdygjrsqtzq2n0yrf2493p83kkfjhx0wlh',
            publicKey: '0279be667ef9dcbbac55a06295ce870b07029bfcdb2dce28d959f2815b16f81798'
          };
          return {
            success: true,
            data: this._currentWallet
          };
        }

        throw new Error('UniSat wallet no est√° instalada');
      }

      // Verificar m√©todos necesarios
      if (!window.unisat.requestAccounts) {
        throw new Error('UniSat wallet no tiene el m√©todo requestAccounts');
      }

      // Solicitar conexi√≥n
      console.log('üìû Solicitando acceso a cuentas...');
      const accounts = await window.unisat.requestAccounts();

      if (!accounts || accounts.length === 0) {
        throw new Error('No se obtuvieron cuentas de UniSat');
      }

      // Obtener clave p√∫blica si est√° disponible
      let publicKey = null;
      if (window.unisat.getPublicKey) {
        try {
          publicKey = await window.unisat.getPublicKey();
          console.log('üîë Clave p√∫blica obtenida');
        } catch (error) {
          console.warn('‚ö†Ô∏è No se pudo obtener la clave p√∫blica:', error);
        }
      }

      this._currentWallet = {
        name: 'UniSat',
        id: 'unisat',
        address: accounts[0],
        publicKey: publicKey
      };

      console.log('‚úÖ UniSat conectada exitosamente:', this._currentWallet.address);

      return {
        success: true,
        data: this._currentWallet
      };

    } catch (error) {
      console.error('‚ùå Error conectando UniSat:', error);
      return {
        success: false,
        error: error.message
      };
    }
  },

  /**
   * Conecta con la wallet OKX
   * @returns {Object} { success: boolean, data?: Object, error?: string }
   */
  async connectOKX() {
    console.log('üîó Intentando conectar con OKX...');

    try {
      // Verificar si OKX est√° disponible
      if (typeof window === 'undefined' || !window.okxwallet || !window.okxwallet.bitcoin) {
        // Simular conexi√≥n en entorno de desarrollo
        if (process.env.NODE_ENV === 'development' || typeof process === 'undefined') {
          console.log('‚ö†Ô∏è Simulando conexi√≥n OKX en desarrollo');
          this._currentWallet = {
            name: 'OKX (Simulado)',
            id: 'okx',
            address: 'bc1qar0srrr7xfkvy5l643lydnw9re59gtzzwf5mdq',
            publicKey: '0279be667ef9dcbbac55a06295ce870b07029bfcdb2dce28d959f2815b16f81798'
          };
          return {
            success: true,
            data: this._currentWallet
          };
        }

        throw new Error('OKX wallet no est√° instalada');
      }

      // Verificar m√©todos necesarios
      if (!window.okxwallet.bitcoin.requestAccounts) {
        throw new Error('OKX wallet no tiene el m√©todo requestAccounts');
      }

      // Solicitar conexi√≥n
      console.log('üìû Solicitando acceso a cuentas...');
      const accounts = await window.okxwallet.bitcoin.requestAccounts();

      if (!accounts || accounts.length === 0) {
        throw new Error('No se obtuvieron cuentas de OKX');
      }

      // Obtener clave p√∫blica si est√° disponible
      let publicKey = null;
      if (window.okxwallet.bitcoin.getPublicKey) {
        try {
          publicKey = await window.okxwallet.bitcoin.getPublicKey();
          console.log('üîë Clave p√∫blica obtenida');
        } catch (error) {
          console.warn('‚ö†Ô∏è No se pudo obtener la clave p√∫blica:', error);
        }
      }

      this._currentWallet = {
        name: 'OKX',
        id: 'okx',
        address: accounts[0],
        publicKey: publicKey
      };

      console.log('‚úÖ OKX conectada exitosamente:', this._currentWallet.address);

      return {
        success: true,
        data: this._currentWallet
      };

    } catch (error) {
      console.error('‚ùå Error conectando OKX:', error);
      return {
        success: false,
        error: error.message
      };
    }
  },

  /**
   * Desconecta la wallet actual
   * @returns {Object} { success: boolean, error?: string }
   */
  disconnect() {
    console.log('üîå Desconectando wallet...');

    try {
      if (this._currentWallet) {
        console.log(`‚úÖ Wallet ${this._currentWallet.name} desconectada`);
        this._currentWallet = null;
      } else {
        console.log('‚ö†Ô∏è No hay wallet conectada');
      }

      return {
        success: true
      };

    } catch (error) {
      console.error('‚ùå Error desconectando wallet:', error);
      return {
        success: false,
        error: error.message
      };
    }
  },

  /**
   * Obtiene el estado actual de la wallet conectada
   * @returns {Object} { success: boolean, data?: Object, error?: string }
   */
  getCurrent() {
    console.log('üìä Obteniendo estado de wallet actual...');

    try {
      if (this._currentWallet) {
        console.log('‚úÖ Wallet conectada:', this._currentWallet.name);
        return {
          success: true,
          data: this._currentWallet
        };
      } else {
        console.log('‚ö†Ô∏è No hay wallet conectada');
        return {
          success: true,
          data: null
        };
      }

    } catch (error) {
      console.error('‚ùå Error obteniendo estado de wallet:', error);
      return {
        success: false,
        error: error.message
      };
    }
  }
};

// Exportar el m√≥dulo
if (typeof module !== 'undefined' && module.exports) {
  module.exports = Wallets;
} else if (typeof window !== 'undefined') {
  window.Wallets = Wallets;
}

