input.content:
/**
 * Estilos para el modal de selecci√≥n de wallets
 * Dise√±ado para Fractal Bitcoin ($FB) y tokens MoonYetis BRC-20
 */

/* Overlay del modal */
.wallet-modal-overlay {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-color: rgba(0, 0, 0, 0.7);
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 10000;
  opacity: 0;
  visibility: hidden;
  transition: all 0.3s ease;
}

.wallet-modal-overlay.wallet-modal-show {
  opacity: 1;
  visibility: visible;
}

.wallet-modal-overlay.wallet-modal-hide {
  opacity: 0;
  visibility: hidden;
}

/* Contenido del modal */
.wallet-modal-content {
  background: #ffffff;
  border-radius: 16px;
  box-shadow: 0 20px 60px rgba(0, 0, 0, 0.3);
  max-width: 480px;
  width: 90%;
  max-height: 90vh;
  overflow-y: auto;
  padding: 32px;
  position: relative;
  transform: scale(0.9);
  transition: transform 0.3s ease;
}

.wallet-modal-show .wallet-modal-content {
  transform: scale(1);
}

.wallet-modal-hide .wallet-modal-content {
  transform: scale(0.9);
}

/* T√≠tulo */
.wallet-modal-title {
  margin: 0 0 8px 0;
  font-size: 24px;
  font-weight: 700;
  color: #1a1a1a;
  text-align: center;
  line-height: 1.2;
}

/* Subt√≠tulo */
.wallet-modal-subtitle {
  margin: 0 0 24px 0;
  font-size: 16px;
  color: #666666;
  text-align: center;
  line-height: 1.4;
}

/* Contenedor de wallets */
.wallet-modal-wallets {
  display: flex;
  flex-direction: column;
  gap: 12px;
  margin-bottom: 24px;
}

/* Botones de wallet */
.wallet-button {
  display: flex;
  align-items: center;
  gap: 16px;
  padding: 16px;
  border: 2px solid #e0e0e0;
  border-radius: 12px;
  background: #ffffff;
  color: #1a1a1a;
  font-family: inherit;
  font-size: 16px;
  cursor: pointer;
  transition: all 0.2s ease;
  text-align: left;
  width: 100%;
  position: relative;
  overflow: hidden;
}

.wallet-button:hover {
  border-color: #f97316;
  background: #fef3e7;
  transform: translateY(-2px);
  box-shadow: 0 4px 12px rgba(249, 115, 22, 0.15);
}

.wallet-button:focus {
  outline: none;
  border-color: #f97316;
  box-shadow: 0 0 0 3px rgba(249, 115, 22, 0.2);
}

.wallet-button:active {
  transform: translateY(0);
}

/* Bot√≥n disponible */
.wallet-button.available {
  border-color: #10b981;
  background: #f0fdf4;
}

.wallet-button.available:hover {
  border-color: #059669;
  background: #dcfce7;
}

/* Bot√≥n no disponible */
.wallet-button.unavailable {
  border-color: #f59e0b;
  background: #fffbeb;
}

.wallet-button.unavailable:hover {
  border-color: #d97706;
  background: #fef3c7;
}

/* Icono de wallet */
.wallet-icon {
  font-size: 32px;
  width: 48px;
  height: 48px;
  display: flex;
  align-items: center;
  justify-content: center;
  background: #f8fafc;
  border-radius: 8px;
  flex-shrink: 0;
}

.wallet-button.available .wallet-icon {
  background: #e6fffa;
}

.wallet-button.unavailable .wallet-icon {
  background: #fef3e7;
}

/* Informaci√≥n de wallet */
.wallet-info {
  flex: 1;
  display: flex;
  flex-direction: column;
  gap: 4px;
  min-width: 0;
}

.wallet-name {
  font-size: 18px;
  font-weight: 600;
  color: #1a1a1a;
  line-height: 1.2;
}

.wallet-description {
  font-size: 14px;
  color: #666666;
  line-height: 1.3;
}

.wallet-status {
  font-size: 12px;
  font-weight: 500;
  text-transform: uppercase;
  letter-spacing: 0.5px;
  margin-top: 4px;
}

.wallet-button.available .wallet-status {
  color: #059669;
}

.wallet-button.unavailable .wallet-status {
  color: #d97706;
}

/* Texto de instalar */
.wallet-install-text {
  position: absolute;
  top: 50%;
  right: 16px;
  transform: translateY(-50%);
  font-size: 14px;
  font-weight: 600;
  color: #f97316;
  background: #ffffff;
  padding: 6px 12px;
  border-radius: 6px;
  border: 1px solid #f97316;
  transition: all 0.2s ease;
}

.wallet-button.unavailable:hover .wallet-install-text {
  background: #f97316;
  color: #ffffff;
}

/* Bot√≥n de cancelar */
.wallet-modal-cancel {
  width: 100%;
  padding: 12px 24px;
  border: 2px solid #e5e7eb;
  border-radius: 8px;
