function createWorkingReels() {
    console.log('üé≤ Creating working reels with PNG images...');

    const container = document.getElementById('reelsContainer');
    if (!container) {
        console.warn('‚ö†Ô∏è Reels container not found');
        return;
    }

    // Clear existing reels
    container.innerHTML = '';

    // Symbol configuration with PNG images and emoji fallbacks
    const SYMBOL_CONFIG = [
        { id: 'yeti', name: 'Yeti Wild', image: 'assets/symbols/yeti-wild.png', emoji: 'üèîÔ∏è', rarity: 'legendary' },
        { id: 'rocket', name: 'Rocket High', image: 'assets/symbols/rocket-high.png', emoji: 'üöÄ', rarity: 'high' },
        { id: 'moon', name: 'Moon Scatter', image: 'assets/symbols/moon-scatter.png', emoji: 'üåô', rarity: 'special' },
        { id: 'coin', name: 'Coin Medium', image: 'assets/symbols/coin-medium.png', emoji: 'ü™ô', rarity: 'medium' },
        { id: 'star', name: 'Star Medium', image: 'assets/symbols/star-medium.png', emoji: '‚≠ê', rarity: 'medium' },
        { id: 'planet', name: 'Planet Low', image: 'assets/symbols/planet-low.png', emoji: 'ü™ê', rarity: 'low' },
        { id: 'alien', name: 'Alien Bonus', image: 'assets/symbols/alien-bonus.png', emoji: 'üëΩ', rarity: 'bonus' },
        { id: 'ufo', name: 'UFO Special', image: 'assets/symbols/ufo-special.png', emoji: 'üõ∏', rarity: 'special' }
    ];

    // Create 5 reels
    for (let i = 0; i < 5; i++) {
        const reel = document.createElement('div');
        reel.className = 'reel';
        reel.id = `reel-${i}`;

        // Create 3 symbols per reel
        for (let j = 0; j < 3; j++) {
            const symbol = document.createElement('div');
            symbol.className = 'symbol';

            // Select random symbol
            const randomSymbolConfig = SYMBOL_CONFIG[Math.floor(Math.random() * SYMBOL_CONFIG.length)];

            // Create image element with fallback
            const img = document.createElement('img');
            img.src = randomSymbolConfig.image;
            img.alt = randomSymbolConfig.name;
            img.title = randomSymbolConfig.name;
            img.dataset.symbolId = randomSymbolConfig.id;
            img.dataset.emoji = randomSymbolConfig.emoji;

            // Image styling
            img.style.width = '50px';
            img.style.height = '50px';
            img.style.objectFit = 'contain';
            img.style.filter = 'drop-shadow(0 2px 4px rgba(0,0,0,0.3))';

            // Error handling - fallback to emoji
            img.onerror = function() {
                console.warn(`‚ö†Ô∏è Failed to load image: ${randomSymbolConfig.image}`);
                this.style.display = 'none';
                const fallback = document.createElement('span');
                fallback.textContent = randomSymbolConfig.emoji;
                fallback.style.fontSize = '2rem';
                fallback.title = randomSymbolConfig.name + ' (fallback)';
                this.parentNode.appendChild(fallback);
            };

            // Symbol container styling
            symbol.style.display = 'flex';
            symbol.style.alignItems = 'center';
            symbol.style.justifyContent = 'center';
            symbol.style.height = '66px';
            symbol.style.borderBottom = '1px solid #374151';
            symbol.style.background = 'linear-gradient(135deg, rgba(255,255,255,0.1) 0%, rgba(255,255,255,0.05) 100%)';
            symbol.style.position = 'relative';

            // Add glow effect for special symbols
            if (randomSymbolConfig.rarity === 'legendary') {
                symbol.style.boxShadow = '0 0 10px rgba(255, 215, 0, 0.5)';
            } else if (randomSymbolConfig.rarity === 'special') {
                symbol.style.boxShadow = '0 0 8px rgba(138, 43, 226, 0.4)';
            }

            symbol.appendChild(img);
            reel.appendChild(symbol);
        }

        container.appendChild(reel);
    }

    console.log('‚úÖ Working reels created with PNG images and fallbacks');

    // Verify images loaded
    setTimeout(() => {
        const images = container.querySelectorAll('img');
        let loadedCount = 0;

