 cd /var/www/moonyetis-slots/frontend
 grep -n -A 15 "Main Connect button clicked" index.html js/*.js

 # 2. Buscar l√≠nea 7604 espec√≠ficamente donde falla
 sed -n '7600,7610p' index.html

 # 3. Buscar la funci√≥n connectWallet que est√° fallando
 grep -n -A 20 -B 5 "Connecting wallet for real game play" index.html
 js/*.js

 # 4. Verificar la l√≥gica de detecci√≥n dentro de la funci√≥n connect
 grep -n -A 10 "window\.unisat\|window\.okxwallet" index.html js/*.js

 # 5. Reemplazar la funci√≥n problem√°tica con detecci√≥n directa
 # Buscar y reemplazar la validaci√≥n incorrecta
 sed -i '/if.*!window\.unisat.*!window\.okxwallet/c\
         // Direct wallet detection - always available since we verified in 
 console\
         const walletAvailable = window.unisat || window.okxwallet;'
 index.html

 # 6. O simplificar la funci√≥n connect para usar detecci√≥n directa
 sed -i '/Connecting wallet for real game play/a\
         console.log("üîç Direct detection:", {unisat: !!window.unisat, okx: 
 !!window.okxwallet});' index.html

 # 7. Crear versi√≥n simplificada de conexi√≥n
 echo "
 // Funci√≥n simplificada de conexi√≥n
 async function connectWalletDirect() {
     if (window.unisat) {
         try {
             const accounts = await window.unisat.requestAccounts();
             console.log('‚úÖ UniSat connected:', accounts);
             return {provider: 'unisat', accounts};
         } catch(e) { console.log('UniSat error:', e); }
     }
     if (window.okxwallet) {
         try {
             const accounts = await 
 window.okxwallet.bitcoin.requestAccounts();
             console.log('‚úÖ OKX connected:', accounts);
             return {provider: 'okx', accounts};
         } catch(e) { console.log('OKX error:', e); }
     }
     console.log('‚ùå No wallets found');
 }
