#!/usr/bin/env node

// Script para corregir problemas de wallet connection en MoonYetis Slots

const fs = require('fs');
const path = require('path');

console.log('üîß Aplicando correcciones de wallet connection...');

const frontendPath = '/var/www/moonyetis-slots/frontend/index.html';

// Leer el archivo actual
let content = fs.readFileSync(frontendPath, 'utf8');

console.log('1Ô∏è‚É£ Quitando modo demo...');

// Quitar el bot√≥n de demo mode
content = content.replace(
    /<button class="modal-btn secondary" onclick="connectWallet\('demo'\)">\s*üéÆ Demo Mode \(Free Play\)\s*<\/button>/g,
    ''
);

// Quitar la descripci√≥n del demo mode
content = content.replace(
    /<p style="font-size: 0\.9rem; color: var\(--gray-400\);">\s*Demo mode allows you to play without connecting a wallet using virtual tokens\.\s*<\/p>/g,
    ''
);

console.log('2Ô∏è‚É£ Corrigiendo l√≥gica de conexi√≥n de wallet...');

// Corregir la URL base de la API
content = content.replace(
    /apiBase: 'http:\/\/localhost:3000\/api'/g,
    "apiBase: window.location.origin + '/api'"
);

// Mejorar el manejo de errores en authenticateWallet
const authenticateWalletFix = `async authenticateWallet(address, publicKey, signature) {
                try {
                    console.log('üîê Authenticating wallet...', { address, walletType: this.walletType });

                    const response = await fetch(\`\${this.apiBase}/blockchain/wallet/connect\`, {
                        method: 'POST',
                        headers: {
                            'Content-Type': 'application/json'
                        },
                        credentials: 'include',
                        body: JSON.stringify({
                            walletType: this.walletType,
                            address,
                            publicKey,
                            signature,
                            message: \`Connect to MoonYetis Slots\\nAddress: \${address}\\nTimestamp: \${Date.now()}\`
                        })
                    });

                    if (!response.ok) {
                        throw new Error(\`HTTP \${response.status}: \${response.statusText}\`);
                    }

                    const result = await response.json();
                    console.log('‚úÖ Authentication result:', result);

                    if (result.success) {
                        this.sessionToken = result.sessionToken;
                        return { success: true, userData: result.userData };
                    } else {
                        throw new Error(result.error || 'Authentication failed');
                    }
                } catch (error) {
                    console.error('‚ùå Authentication error:', error);
                    return { 
                        success: false, 
                        error: \`Authentication failed: \${error.message}\`
                    };
                }
            }`;

// Reemplazar la funci√≥n authenticateWallet
content = content.replace(
    /async authenticateWallet\(address, publicKey, signature\) \{[\s\S]*?\n            \}/,
    authenticateWalletFix
);

console.log('3Ô∏è‚É£ Mejorando requestWalletConnection...');

const requestWalletConnectionFix = `async requestWalletConnection(walletType, provider) {
                try {
                    console.log('üì± Requesting wallet connection...', walletType);

                    // Request account access
                    const accounts = await provider.requestAccounts();
                    if (!accounts || accounts.length === 0) {
                        throw new Error('No accounts available');
                    }

                    const address = accounts[0];
                    console.log('üìç Wallet address:', address);

                    // Get public key
                    let publicKey = '';
                    try {
                        publicKey = await provider.getPublicKey();
                    } catch (err) {
                        console.warn('‚ö†Ô∏è Could not get public key:', err.message);
                        publicKey = 'not_available';
                    }

                    // Create message to sign for authentication
                    const timestamp = Date.now();
                    const message = \`Connect to MoonYetis Slots\\nAddress: \${address}\\nTimestamp: \${timestamp}\`;

                    console.log('‚úçÔ∏è Requesting signature...');

                    // Request signature
                    const signature = await provider.signMessage(message);

                    console.log('‚úÖ Wallet connection successful');

                    return {
                        success: true,
                        address,
                        publicKey,
                        signature,
                        message
                    };
                } catch (error) {
                    console.error('‚ùå Wallet connection error:', error);
                    return {
                        success: false,
                        error: error.message || 'Failed to connect wallet'
                    };
                }
            }`;

// Reemplazar la funci√≥n requestWalletConnection
content = content.replace(
    /async requestWalletConnection\(walletType, provider\) \{[\s\S]*?\n            \}/,
    requestWalletConnectionFix
);

console.log('4Ô∏è‚É£ Quitando l√≥gica de demo mode...');

// Quitar la condici√≥n de demo mode del connectWallet
content = content.replace(
    /if \(walletType === 'demo'\) \{\s*return this\.connectDemoMode\(\);\s*\}/g,
    ''
);

// Quitar la funci√≥n connectDemoMode completa
content = content.replace(
    /\/\/ Demo mode connection\s*connectDemoMode\(\) \{[\s\S]*?\n            \}/,
    ''
);

console.log('5Ô∏è‚É£ Mejorando manejo de errores globales...');

// Agregar mejor logging en connectWallet
content = content.replace(
    /console\.error\('Wallet connection failed:', error\);/g,
    `console.error('‚ùå Wallet connection failed:', error);

                    // Mostrar error espec√≠fico al usuario
                    let errorMessage = 'Connection failed';
                    if (error.message.includes('not installed')) {
                        errorMessage = 'Wallet extension not found. Please install the wallet extension.';
                    } else if (error.message.includes('rejected')) {
                        errorMessage = 'Connection was rejected. Please approve the connection in your wallet.';
                    } else if (error.message.includes('Authentication failed')) {
                        errorMessage = 'Authentication failed. Please try connecting again.';
                    } else {
                        errorMessage = \`Connection error: \${error.message}\`;
                    }

                    this.showErrorMessage(errorMessage);`
);

console.log('6Ô∏è‚É£ Escribiendo archivo corregido...');

// Escribir el archivo corregido
fs.writeFileSync(frontendPath, content, 'utf8');

console.log('‚úÖ Correcciones aplicadas exitosamente!');
console.log('');
console.log('üìã Cambios realizados:');
console.log('   ‚Ä¢ ‚ùå Modo demo removido completamente');
console.log('   ‚Ä¢ üîß URL de API corregida para producci√≥n');
console.log('   ‚Ä¢ üîê Autenticaci√≥n de wallet mejorada');
console.log('   ‚Ä¢ üì± Manejo de conexi√≥n optimizado');
console.log('   ‚Ä¢ üö® Mensajes de error m√°s descriptivos');
console.log('');
console.log('üîÑ Reiniciando aplicaci√≥n...');

// Reiniciar PM2
const { exec } = require('child_process');
exec('sudo -u deploy pm2 reload moonyetis-slots', (error, stdout, stderr) => {
    if (error) {
        console.error('Error restarting PM2:', error);
    } else {
        console.log('‚úÖ Aplicaci√≥n reiniciada');
        console.log('üåê Cambios activos en: http://moonyetis.io');
    }
});

