PASO 5: Configurar Nginx
cd /var/www/moonyetis-slots
# Verificar si existe configuración de Nginx
ls -la nginx*.conf
# Crear configuración Nginx
sudo tee /etc/nginx/sites-available/moonyetis << 'EOF'
server {
listen 80;
server_name 168.231.124.18;
# Headers anti-caché
add_header Cache-Control "no-cache, no-store, must-revalidate";
add_header Pragma "no-cache";
add_header Expires "0";
# Servir archivos estáticos desde el frontend
location / {
root /var/www/moonyetis-slots/frontend;
try_files $uri $uri/ @backend;
index index.html;
}
# Proxy para APIs al backend
location /api/ {
proxy_pass http://localhost:3000;
proxy_http_version 1.1;
proxy_set_header Upgrade $http_upgrade;
proxy_set_header Connection 'upgrade';
proxy_set_header Host $host;
proxy_set_header X-Real-IP $remote_addr;
proxy_set_header X-Forwarded-For $proxy_add_x_forwarded_for;
proxy_set_header X-Forwarded-Proto $scheme;
proxy_cache_bypass $http_upgrade;
}
# WebSocket para socket.io
location /socket.io/ {
proxy_pass http://localhost:3000;
proxy_http_version 1.1;
proxy_set_header Upgrade $http_upgrade;
proxy_set_header Connection "upgrade";
proxy_set_header Host $host;
proxy_set_header X-Real-IP $remote_addr;
proxy_set_header X-Forwarded-For $proxy_add_x_forwarded_for;
proxy_set_header X-Forwarded-Proto $scheme;
}
# Fallback al backend si archivo no existe
location @backend {
proxy_pass http://localhost:3000;
proxy_set_header Host $host;
proxy_set_header X-Real-IP $remote_addr;
proxy_set_header X-Forwarded-For $proxy_add_x_forwarded_for;
proxy_set_header X-Forwarded-Proto $scheme;
}
}
EOF
# Habilitar configuración
sudo ln -sf /etc/nginx/sites-available/moonyetis /etc/nginx/sites-enabled/
sudo rm -f /etc/nginx/sites-enabled/default
# Test y restart Nginx
sudo nginx -t
sudo systemctl restart nginx
sudo systemctl status nginx
# Test final
curl -I http://168.231.124.18

