// Demo mode connection
async connectDemoMode() {
    this.connectedWallet = 'demo';
    this.walletType = 'demo';
    const demoBalance = await this.getMoonyetisBalance();
    this.accountInfo = {
        gameChips: demoBalance,
        walletAddress: 'demo_address',
        totalDeposited: 0,
        totalWithdrawn: 0
    };

    gameState.balance = demoBalance;
    gameState.connectedWallet = 'demo';
    gameState.demoMode = true;
    gameState.realBlockchain = false;

    this.showSuccessMessage(`Demo mode activated! ${demoBalance.toLocaleString()} MOONYETIS tokens loaded.`);
    return { success: true, address: 'demo' };
}

