    97‚Üí        }
    98‚Üí        
    99‚Üí        // Check if BlockchainIntegration class exists (from main HTML)
   100‚Üí        if (typeof window.blockchainIntegration !== 'undefined') {
   101‚Üí            const result = await window.blockchainIntegration.connectWallet(walletType);
   102‚Üí            if (result.success) {
   103‚Üí                gameState.connectedWallet = true;
   104‚Üí                gameState.balance = result.balance || 0;
   105‚Üí                
