   107‚Üí                if (walletInfo) {
   108‚Üí                    walletInfo.innerHTML = `<span style="color: #4ECDC4;">‚úÖ ${result.walletName} Conectada</span>`;
   109‚Üí                }
   110‚Üí                
   111‚Üí                const balance = document.getElementById('balance');
   112‚Üí                if (balance) {
