   164‚Üí// Ajustar apuesta
   165‚Üífunction adjustBet(change) {
   166‚Üí    const newBet = gameState.currentBet + change;
   167‚Üí    if (newBet >= 5 && newBet <= 100) {
   168‚Üí        gameState.currentBet = newBet;
   169‚Üí        updateUI();
   170‚Üí        console.log('üí∞ Apuesta ajustada a: ' + gameState.currentBet + ' MY');
   171‚Üí    }
   172‚Üí}
   173‚Üí
   174‚Üí// Ajustar l√≠neas
   175‚Üífunction adjustLines(change) {
   176‚Üí    const newLines = gameState.activeLines + change;
   177‚Üí    if (newLines >= 1 && newLines <= 25) {
   178‚Üí        gameState.activeLines = newLines;
   179‚Üí        updateUI();
   180‚Üí        console.log('üìä L√≠neas ajustadas a: ' + gameState.activeLines);
   181‚Üí    }
   182‚Üí}
   183‚Üí
   184‚Üí// Funci√≥n principal de giro
   185‚Üífunction spin() {
   186‚Üí    if (!gameState.connectedWallet) {
   187‚Üí        showMessage('‚ö†Ô∏è ¬°Conecta tu wallet primero!', 'lose');
   188‚Üí        return;
   189‚Üí    }
   190‚Üí
   191‚Üí    if (gameState.isSpinning) {
   192‚Üí        console.log('‚ö†Ô∏è Ya hay un giro en progreso');
   193‚Üí        return;
   194‚Üí    }
   195‚Üí
   196‚Üí    const totalBet = gameState.currentBet * gameState.activeLines;
   197‚Üí    
   198‚Üí    if (gameState.balance < totalBet) {
   199‚Üí        showMessage('üí∏ ¬°Saldo insuficiente!', 'lose');
   200‚Üí        return;
   201‚Üí    }
   202‚Üí
   203‚Üí    console.log('üé∞ Iniciando giro...');
   204‚Üí    gameState.isSpinning = true;
   205‚Üí    gameState.balance -= totalBet;
   206‚Üí    gameState.totalSpins++;
   207‚Üí    gameState.totalWagered += totalBet;
   208‚Üí    
   209‚Üí    updateUI();
   210‚Üí
   211‚Üí    // Animar rodillos
   212‚Üí    const reels = document.querySelectorAll('.reel');
   213‚Üí    reels.forEach(function(reel) {
   214‚Üí        reel.classList.add('spinning');
   215‚Üí    });
   216‚Üí
   217‚Üí    // Simular tiempo de giro
   218‚Üí    setTimeout(function() {
   219‚Üí        reels.forEach(function(reel) {
   220‚Üí            reel.classList.remove('spinning');
   221‚Üí        });
   222‚Üí
   223‚Üí        const results = generateResults();
   224‚Üí        displayResults(results);
   225‚Üí        
   226‚Üí        const winAmount = calculateWin(results);
   227‚Üí        
   228‚Üí        if (winAmount > 0) {
   229‚Üí            gameState.balance += winAmount;
   230‚Üí            gameState.totalWon += winAmount;
   231‚Üí            
   232‚Üí            if (winAmount > gameState.biggestWin) {
   233‚Üí                gameState.biggestWin = winAmount;
   234‚Üí            }
   235‚Üí            
   236‚Üí            showMessage('üéâ ¬°GANASTE ' + winAmount + ' MY!', 'win');
   237‚Üí            highlightWinningSymbols();
   238‚Üí            
   239‚Üí            console.log('üèÜ Ganancia: ' + winAmount + ' MY');
   240‚Üí        } else {
   241‚Üí            showMessage('üòî Sin suerte esta vez...', 'lose');
   242‚Üí            console.log('üòî Sin ganancia');
   243‚Üí        }
   244‚Üí
   245‚Üí        gameState.isSpinning = false;
   246‚Üí        updateUI();
   247‚Üí        
   248‚Üí    }, 2000);
   249‚Üí}
   250‚Üí
   251‚Üí// Generar resultados aleatorios
   252‚Üífunction generateResults() {
   253‚Üí    const results = [];
   254‚Üí    for (let i = 0; i < 5; i++) {
   255‚Üí        const reel = [];
   256‚Üí        for (let j = 0; j < 3; j++) {
   257‚Üí            reel.push(SYMBOLS[Math.floor(Math.random() * SYMBOLS.length)]);
   258‚Üí        }
   259‚Üí        results.push(reel);
   260‚Üí    }
   261‚Üí    console.log('üé≤ Resultados generados:', results);
   262‚Üí    return results;
   263‚Üí}
   264‚Üí
   265‚Üí// Mostrar resultados
   266‚Üífunction displayResults(results) {
   267‚Üí    for (let i = 0; i < 5; i++) {
   268‚Üí        const reel = document.getElementById('reel-' + i);
   269‚Üí        if (!reel) continue;
   270‚Üí        
   271‚Üí        const symbols = reel.querySelectorAll('.symbol');
   272‚Üí        
   273‚Üí        for (let j = 0; j < 3; j++) {
   274‚Üí            if (symbols[j]) {
   275‚Üí                const symbolData = results[i][j];
   276‚Üí                // Clear existing content and classes
   277‚Üí                symbols[j].innerHTML = '';
   278‚Üí                symbols[j].className = 'symbol';
   279‚Üí                
   280‚Üí                // Apply symbol-specific CSS class based on name
   281‚Üí                const symbolClass = `symbol-${symbolData.name.replace('-high', '').replace('-medium', '').replace('-low', '').replace('-wild', '').replace('-scatter', '').replace('-bonus', '').replace('-special', '')}`;
   282‚Üí                symbols[j].classList.add(symbolClass);
   283‚Üí                symbols[j].setAttribute('data-symbol', symbolData.name);
   284‚Üí            }
   285‚Üí        }
   286‚Üí    }
   287‚Üí}
   288‚Üí
   289‚Üí// Calcular ganancia simple
   290‚Üífunction calculateWin(results) {
   291‚Üí    const centerLine = [results[0][1], results[1][1], results[2][1], results[3][1], results[4][1]];
   292‚Üí    
   293‚Üí    let consecutiveCount = 1;
   294‚Üí    let symbol = centerLine[0];
   295‚Üí    
   296‚Üí    for (let i = 1; i < centerLine.length; i++) {
   297‚Üí        if (centerLine[i].name === symbol.name || symbol.name === 'yeti-wild') {
   298‚Üí            consecutiveCount++;
   299‚Üí        } else {
   300‚Üí            break;
   301‚Üí        }
   302‚Üí    }
   303‚Üí
   304‚Üí    let multiplier = 0;
   305‚Üí    if (consecutiveCount >= 5) multiplier = 50;
   306‚Üí    else if (consecutiveCount >= 4) multiplier = 20;
   307‚Üí    else if (consecutiveCount >= 3) multiplier = 5;
   308‚Üí
   309‚Üí    if (symbol.name === 'yeti-wild') multiplier *= 2;
   310‚Üí    if (symbol.name === 'rocket-high') multiplier *= 1.5;
   311‚Üí
   312‚Üí    return Math.floor(gameState.currentBet * multiplier);
   313‚Üí}
   314‚Üí
   315‚Üí// Resaltar s√≠mbolos ganadores
   316‚Üífunction highlightWinningSymbols() {
   317‚Üí    const reels = document.querySelectorAll('.reel');
   318‚Üí    reels.forEach(function(reel, index) {
   319‚Üí        const centerSymbol = reel.children[1];
   320‚Üí        if (centerSymbol) {
   321‚Üí            centerSymbol.classList.add('winning');
   322‚Üí        }
   323‚Üí    });
   324‚Üí
   325‚Üí    setTimeout(function() {
   326‚Üí        document.querySelectorAll('.symbol.winning').forEach(function(symbol) {
   327‚Üí            symbol.classList.remove('winning');
   328‚Üí        });
   329‚Üí    }, 3000);
   330‚Üí}
   331‚Üí
   332‚Üí// Mostrar mensaje
   333‚Üífunction showMessage(text, type) {
   334‚Üí    const messageEl = document.getElementById('message');
   335‚Üí    if (!messageEl) return;
   336‚Üí    
   337‚Üí    messageEl.textContent = text;
   338‚Üí    messageEl.className = 'message ' + type;
   339‚Üí    messageEl.style.display = 'block';
   340‚Üí    
   341‚Üí    setTimeout(function() {
   342‚Üí        messageEl.style.display = 'none';
   343‚Üí    }, 4000);
   344‚Üí}
   345‚Üí
   346‚Üí// Actualizar interfaz
   347‚Üífunction updateUI() {
   348‚Üí    const elements = {
   349‚Üí        'betAmount': gameState.currentBet + ' MY',
   350‚Üí        'activeLines': gameState.activeLines,
   351‚Üí        'totalBet': (gameState.currentBet * gameState.activeLines) + ' MY',
   352‚Üí        'balanceAmount': gameState.balance,
   353‚Üí        'totalSpins': gameState.totalSpins,
   354‚Üí        'totalWagered': gameState.totalWagered + ' MY',
   355‚Üí        'totalWon': gameState.totalWon + ' MY',
   356‚Üí        'biggestWin': gameState.biggestWin + ' MY',
   357‚Üí        'lastWin': gameState.biggestWin + ' MY'
   358‚Üí    };
   359‚Üí
   360‚Üí    for (let id in elements) {
   361‚Üí        const element = document.getElementById(id);
   362‚Üí        if (element) {
   363‚Üí            element.textContent = elements[id];
   364‚Üí        }
   365‚Üí    }
   366‚Üí
   367‚Üí    const spinBtn = document.getElementById('spinBtn');
   368‚Üí    if (spinBtn) {
   369‚Üí        spinBtn.disabled = gameState.isSpinning;
   370‚Üí        spinBtn.textContent = gameState.isSpinning ? 'üîÑ GIRANDO...' : 'üé∞ GIRAR';
   371‚Üí    }
   372‚Üí}
   373‚Üí
   374‚Üí// Controles de teclado
   375‚Üídocument.addEventListener('keydown', function(e) {
   376‚Üí    if (e.code === 'Space' && !gameState.isSpinning) {
   377‚Üí        e.preventDefault();
   378‚Üí        spin();
   379‚Üí    }
   380‚Üí});
   381‚Üí
   382‚Üí// Exponer funciones globalmente
   383‚Üíwindow.connectWallet = connectWallet;
   384‚Üíwindow.adjustBet = adjustBet;
   385‚Üíwindow.adjustLines = adjustLines;
   386‚Üíwindow.spin = spin;
   387‚Üí
   388‚Üí// Inicializar cuando cargue la p√°gina
   389‚Üídocument.addEventListener('DOMContentLoaded', function() {
   390‚Üí    console.log('üìÑ DOM cargado, inicializando juego...');
   391‚Üí    initGame();
   392‚Üí});
   393‚Üí
   394‚Üíconsole.log('‚úÖ MoonYetis Slots Script Cargado Completamente');
