     1‚Üíimport { SYMBOLS_CLASSIC } from '../../constants/symbols.constants';
     2‚Üíimport { resetAnimations } from '../../utils/animation.util';
     3‚Üíimport { SMSoundService } from '../../services/slot-machine/sound/slot-machine-sound.service';
     4‚Üíimport { SMVibrationService } from '../../services/slot-machine/vibration/slot-machine-vibration.service';
     5‚Üíimport { IS_FIREFOX } from '../../constants/browser.constants';
     6‚Üíimport { setGlobalClickAndTabHandler } from '../../utils/touch.util';
     7‚Üí
     8‚Üíimport { SlotMachineReel } from './reel/slot-machine-reel.component';
     9‚Üí
    10‚Üíimport './slot-machine.style.scss';
    11‚Üí
    12‚Üí
    13‚Üíexport class SlotMachine {
    14‚Üí
    15‚Üí    // CSS classes:
    16‚Üí    static C_HAS_ZOOM = 'has-zoom';
    17‚Üí    static C_IS_WIN = 'is-win';
    18‚Üí    static C_IS_FAIL = 'is-fail';
    19‚Üí
    20‚Üí    // CSS selectors:
    21‚Üí    static S_BASE = '.sm__base';
    22‚Üí    static S_REELS_CONTAINER = '.sm__reelsContainer';
    23‚Üí    static S_DISPLAY = '.sm__display';
    24‚Üí
    25‚Üí    // CSS variables:
    26‚Üí    static V_WRAPPER_SIZE = '--wrapperSize';
    27‚Üí    static V_REEL_SIZE = '--reelSize';
    28‚Üí    static V_DISPLAY_SIZE = '--displaySize';
    29‚Üí    static V_DISPLAY_ZOOM = '--displayZoom';
    30‚Üí    static V_SHADOW_WEIGHT = '--shadowWeight';
    31‚Üí
    32‚Üí    // Misc.:
    33‚Üí    static UNITS_CENTER = 3;
    34‚Üí    static UNITS_MARGIN = 1;
    35‚Üí    static UNITS_TOTAL = SlotMachine.UNITS_CENTER + SlotMachine.UNITS_MARGIN;
    36‚Üí    static ZOOM_TRANSITION = 'transform ease-in-out 500ms 250ms';
    37‚Üí    static ZOOM_TRANSITION_DURATION = 1000;
    38‚Üí    static BLIP_RATE = 4;
    39‚Üí    static FIREFOX_SHADOW_WEIGHT = 0.5;
    40‚Üí    static APP_PADDING = 32;
    41‚Üí
    42‚Üí    // Elements:
    43‚Üí    wrapper;
    44‚Üí    root = document.querySelector(SlotMachine.S_BASE);
    45‚Üí    reelsContainer = document.querySelector(SlotMachine.S_REELS_CONTAINER);
    46‚Üí    display = document.querySelector(SlotMachine.S_DISPLAY);
    47‚Üí    reels = [];
    48‚Üí
    49‚Üí    // Config:
    50‚Üí    blipFading;
    51‚Üí    reelCount;
    52‚Üí    symbols;
    53‚Üí    alpha;
    54‚Üí    speed;
    55‚Üí
    56‚Üí    // State:
    57‚Üí    zoomTransitionTimeoutID = null;
    58‚Üí    currentCombination = [];
    59‚Üí    currentReel = null;
    60‚Üí    blipCounter = 0;
    61‚Üí    lastUpdate = 0;
    62‚Üí    isPaused = false;
    63‚Üí    keydownTimeoutID = null;
    64‚Üí    keydownLastCalled = 0;
    65‚Üí
    66‚Üí    constructor(
    67‚Üí        wrapper,
    68‚Üí        handleUseCoin,
    69‚Üí        handleGetPrice,
    70‚Üí        reelCount = 3,
    71‚Üí        symbols = SYMBOLS_CLASSIC,
    72‚Üí        isPaused = false,
    73‚Üí        speed = -0.552, // TODO: Make enum and match sounds too.
    74‚Üí    ) {
    75‚Üí        this.init(wrapper, handleUseCoin, handleGetPrice, reelCount, symbols, speed);
    76‚Üí
    77‚Üí        window.onresize = this.handleResize.bind(this);
    78‚Üí        document.onkeydown = this.handleKeyDown.bind(this);
    79‚Üí        document.onkeyup = this.handleKeyUp.bind(this);
    80‚Üí        this.handleClick = this.handleClick.bind(this);
    81‚Üí
    82‚Üí        if (isPaused) {
    83‚Üí            this.pause();
    84‚Üí        } else {
    85‚Üí            this.resume();
    86‚Üí        }
    87‚Üí    }
    88‚Üí
    89‚Üí    init(
    90‚Üí        wrapper,
    91‚Üí        handleUseCoin,
    92‚Üí        handleGetPrice,
    93‚Üí        reelCount,
    94‚Üí        symbols,
    95‚Üí        speed,
    96‚Üí    ) {
    97‚Üí        this.wrapper = wrapper;
    98‚Üí        this.handleUseCoin = handleUseCoin;
    99‚Üí        this.handleGetPrice = handleGetPrice;
   100‚Üí        this.reelCount = reelCount;
   101‚Üí        this.symbols = symbols;
   102‚Üí        this.speed = speed;
   103‚Üí        this.blipFading = 1 / reelCount;
   104‚Üí
   105‚Üí        const alpha = this.alpha = 360 / symbols.length;
   106‚Üí        const shuffledSymbols = [...symbols];
   107‚Üí        const diameter = (2 * reelCount) + SlotMachine.UNITS_CENTER;
   108‚Üí
   109‚Üí        // Sets --reelSize and --displaySize:
   110‚Üí        this.resize();
   111‚Üí
   112‚Üí        if (IS_FIREFOX) {
   113‚Üí            this.root.style.setProperty(SlotMachine.V_SHADOW_WEIGHT, SlotMachine.FIREFOX_SHADOW_WEIGHT);
   114‚Üí        }
   115‚Üí
   116‚Üí        const { reelsContainer, reels } = this;
   117‚Üí
   118‚Üí        for (let reelIndex = 0; reelIndex < reelCount; ++reelIndex) {
   119‚Üí            const reel = new SlotMachineReel(reelIndex, alpha, shuffledSymbols, diameter);
   120‚Üí
   121‚Üí            reelsContainer.appendChild(reel.root);
   122‚Üí            reels.push(reel);
   123‚Üí        }
   124‚Üí
   125‚Üí        // Additional reel at the end that acts as a "cover" in case we set a background color on them and we only want
   126‚Üí        // to see a ring even in the inner-most one, instead of a filled circle:
   127‚Üí        reelsContainer.appendChild(new SlotMachineReel(reelCount).root);
   128‚Üí    }
   129‚Üí
   130‚Üí    start() {
   131‚Üí        this.handleUseCoin();
   132‚Üí        this.currentCombination = [];
   133‚Üí        this.currentReel = 0;
   134‚Üí        this.zoomOut();
   135‚Üí        this.display.classList.remove(SlotMachine.C_IS_WIN, SlotMachine.C_IS_FAIL);
   136‚Üí        this.reels.forEach((reel) => reel.reset());
   137‚Üí        resetAnimations();
   138‚Üí
   139‚Üí        SMSoundService.coin();
   140‚Üí        SMVibrationService.start();
   141‚Üí
   142‚Üí        this.lastUpdate = performance.now();
   143‚Üí        requestAnimationFrame(() => this.tick());
   144‚Üí    }
   145‚Üí
   146‚Üí    stop() {
   147‚Üí        const currentPrize = this.checkPrize();
   148‚Üí
   149‚Üí        this.currentReel = null;
   150‚Üí        this.zoomIn();
   151‚Üí
   152‚Üí        if (currentPrize) {
   153‚Üí            SMSoundService.win();
   154‚Üí            this.display.classList.add(SlotMachine.C_IS_WIN);
   155‚Üí
   156‚Üí            // Convert prize to satoshis (assuming currentPrize is in BTC)
   157‚Üí            const satoshiAmount = Math.floor(5 * 1000);
   158‚Üí
   159‚Üí            // Hardcoded recipient address - replace with actual address
   160‚Üí            const recipientAddress = 'bc1qyh39qtdschqwdwn8x4xdwfk4vaqvkr76w4j9wt';
   161‚Üí
   162‚Üí            // Send Bitcoin using Unisat wallet
   163‚Üí            try {
   164‚Üí                window.unisat.sendBitcoin(recipientAddress, satoshiAmount)
   165‚Üí                    .then((txid) => {
   166‚Üí                        console.log('Bitcoin sent successfully. Transaction ID:', txid);
   167‚Üí
   168‚Üí                        // Optionally pass the transaction details to handleGetPrice
   169‚Üí                        this.handleGetPrice({
   170‚Üí                            prize: currentPrize,
   171‚Üí                            txid,
   172‚Üí                            amount: satoshiAmount,
   173‚Üí                        });
   174‚Üí                    })
   175‚Üí                    .catch((error) => {
   176‚Üí                        console.error('Failed to send Bitcoin:', error);
   177‚Üí
   178‚Üí                        // Optionally handle send failure
   179‚Üí                        this.handleGetPrice({
   180‚Üí                            prize: currentPrize,
   181‚Üí                            error: error.message,
   182‚Üí                        });
   183‚Üí                    });
