// MoonYetis Slots - Game Logic
console.log('üöÄ MoonYetis Slots Script Loading...');

// Configuraci√≥n del juego
const SYMBOLS = [
    { emoji: 'üèîÔ∏è', image: 'assets/symbols/yeti-wild.png', name: 'yeti-wild' },
    { emoji: 'üöÄ', image: 'assets/symbols/rocket-high.png', name: 'rocket-high' },
    { emoji: 'üåô', image: 'assets/symbols/moon-scatter.png', name: 'moon-scatter' },
    { emoji: 'ü™ô', image: 'assets/symbols/coin-medium.png', name: 'coin-medium' },
    { emoji: '‚≠ê', image: 'assets/symbols/star-medium.png', name: 'star-medium' },
    { emoji: 'ü™ê', image: 'assets/symbols/planet-low.png', name: 'planet-low' },
    { emoji: 'üëΩ', image: 'assets/symbols/alien-bonus.png', name: 'alien-bonus' },
    { emoji: 'üõ∏', image: 'assets/symbols/ufo-special.png', name: 'ufo-special' }
];

// Estado del juego
let gameState = {
    balance: 0,
    currentBet: 10,
    activeLines: 25,
    isSpinning: false,
    connectedWallet: false,
    totalSpins: 0,
    totalWagered: 0,
    totalWon: 0,
    biggestWin: 0
};

// Inicializar juego
function initGame() {
    console.log('üé∞ Inicializando MoonYetis Slots...');
    createReels();
    updateUI();
    console.log('‚úÖ Juego inicializado correctamente');
}

// Crear rodillos
function createReels() {
    const container = document.getElementById('reelsContainer');
    if (!container) {
        console.error('‚ùå No se encontr√≥ el contenedor de rodillos');
        return;
    }

    container.innerHTML = '';

    for (let i = 0; i < 5; i++) {
        const reel = document.createElement('div');
        reel.className = 'reel';
        reel.id = `reel-${i}`;

        for (let j = 0; j < 3; j++) {
            const symbol = document.createElement('div');
            const randomSymbol = SYMBOLS[Math.floor(Math.random() * SYMBOLS.length)];

            // Apply symbol-specific CSS class
            const symbolClass = `symbol-${randomSymbol.name.replace('-high', '').replace('-medium', '').replace('-low', '').replace('-wild', '').replace('-scatter', '').replace('-bonus', '').replace('-special', '')}`;
            symbol.className = `symbol ${symbolClass}`;
            symbol.setAttribute('data-symbol', randomSymbol.name);

            reel.appendChild(symbol);
        }

        container.appendChild(reel);
    }
    console.log('üé≤ Rodillos creados correctamente');
}

// Conectar wallet (real integration)
async function connectWallet(walletType = 'unisat') {
    console.log(`üîó Conectando wallet ${walletType}...`);

    try {
        // Check if MCP blockchain integration is available
        if (typeof window.moonyetisMcp !== 'undefined') {
            console.log('üî• Usando MCP personalizado de MoonYetis');
            const result = await window.moonyetisMcp.connectWallet(walletType);
            if (result.success) {
                gameState.connectedWallet = true;
                gameState.balance = result.balance || 0;
                gameState.walletAddress = result.address;

                const walletInfo = document.getElementById('walletInfo');
                if (walletInfo) {
                    walletInfo.innerHTML = `<span style="color: #4ECDC4;">‚úÖ ${result.walletName} Conectada</span>`;
                }

                const balance = document.getElementById('balance');
                if (balance) {
                    balance.style.display = 'block';
                }

                updateUI();
                showMessage(`üéâ ¬°${result.walletName} conectada! Balance: ${result.balance} MY`, 'win');
                return;
            }
        }

        // Check if BlockchainIntegration class exists (from main HTML)
        if (typeof window.blockchainIntegration !== 'undefined') {
            const result = await window.blockchainIntegration.connectWallet(walletType);
            if (result.success) {
                gameState.connectedWallet = true;
                gameState.balance = result.balance || 0;

                const walletInfo = document.getElementById('walletInfo');
                if (walletInfo) {
                    walletInfo.innerHTML = `<span style="color: #4ECDC4;">‚úÖ ${result.walletName} Conectada</span>`;
                }

                const balance = document.getElementById('balance');
                if (balance) {
                    balance.style.display = 'block';
                }

                updateUI();
                showMessage(`üéâ ¬°${result.walletName} conectada! Balance: ${result.balance} MY`, 'win');
                return;
            }
        }

        // Fallback to basic wallet detection
        let provider = null;
        let walletName = '';

        if (walletType === 'unisat' && typeof window.unisat !== 'undefined') {
            provider = window.unisat;
            walletName = 'UniSat Wallet';
        } else if (walletType === 'okx' && typeof window.okxwallet?.bitcoin !== 'undefined') {
            provider = window.okxwallet.bitcoin;
            walletName = 'OKX Wallet';
        }

        if (provider) {
            const accounts = await provider.requestAccounts();
            if (accounts && accounts.length > 0) {
                gameState.connectedWallet = true;
                gameState.balance = 1000; // Demo balance for now

                const walletInfo = document.getElementById('walletInfo');
                if (walletInfo) {
                    walletInfo.innerHTML = `<span style="color: #4ECDC4;">‚úÖ ${walletName} Conectada</span>`;
                }

                const balance = document.getElementById('balance');
                if (balance) {
                    balance.style.display = 'block';
                }

                updateUI();
                showMessage(`üéâ ¬°${walletName} conectada! Balance: 1000 MY`, 'win');
                return;
            }
        }

        throw new Error(`${walletType} wallet not found or connection failed`);

    } catch (error) {
        console.error('‚ùå Error conectando wallet:', error);
        showMessage(`‚ùå Error conectando wallet: ${error.message}`, 'lose');
    }
}

// Ajustar apuesta
function adjustBet(change) {
    const newBet = gameState.currentBet + change;
    if (newBet >= 5 && newBet <= 100) {
        gameState.currentBet = newBet;
        updateUI();
        console.log('üí∞ Apuesta ajustada a: ' + gameState.currentBet + ' MY');
    }
}

// Ajustar l√≠neas
function adjustLines(change) {
    const newLines = gameState.activeLines + change;
    if (newLines >= 1 && newLines <= 25) {
        gameState.activeLines = newLines;
        updateUI();
        console.log('üìä L√≠neas ajustadas a: ' + gameState.activeLines);
    }
}

// Funci√≥n principal de giro
function spin() {
    if (!gameState.connectedWallet) {
        showMessage('‚ö†Ô∏è ¬°Conecta tu wallet primero!', 'lose');
        return;
    }

    if (gameState.isSpinning) {
        console.log('‚ö†Ô∏è Ya hay un giro en progreso');
        return;
    }

    const totalBet = gameState.currentBet * gameState.activeLines;

    if (gameState.balance < totalBet) {
        showMessage('üí∏ ¬°Saldo insuficiente!', 'lose');
        return;
    }

    console.log('üé∞ Iniciando giro...');
    gameState.isSpinning = true;
    gameState.balance -= totalBet;
    gameState.totalSpins++;
    gameState.totalWagered += totalBet;

    updateUI();

    // Animar rodillos
    const reels = document.querySelectorAll('.reel');
    reels.forEach(function(reel) {
        reel.classList.add('spinning');
    });

    // Simular tiempo de giro
    setTimeout(function() {
        reels.forEach(function(reel) {
            reel.classList.remove('spinning');
        });

        const results = generateResults();
        displayResults(results);

        const winAmount = calculateWin(results);

        if (winAmount > 0) {
            gameState.balance += winAmount;
            gameState.totalWon += winAmount;

            if (winAmount > gameState.biggestWin) {
                gameState.biggestWin = winAmount;
            }

            showMessage('üéâ ¬°GANASTE ' + winAmount + ' MY!', 'win');
            highlightWinningSymbols();

            console.log('üèÜ Ganancia: ' + winAmount + ' MY');
        } else {
            showMessage('üòî Sin suerte esta vez...', 'lose');
            console.log('üòî Sin ganancia');
        }

        gameState.isSpinning = false;
        updateUI();

    }, 2000);
}

// Generar resultados aleatorios
function generateResults() {
    const results = [];
    for (let i = 0; i < 5; i++) {
        const reel = [];
        for (let j = 0; j < 3; j++) {
            reel.push(SYMBOLS[Math.floor(Math.random() * SYMBOLS.length)]);
        }
        results.push(reel);
    }
    console.log('üé≤ Resultados generados:', results);
    return results;
}

// Mostrar resultados
function displayResults(results) {
    for (let i = 0; i < 5; i++) {
        const reel = document.getElementById('reel-' + i);
        if (!reel) continue;

        const symbols = reel.querySelectorAll('.symbol');

        for (let j = 0; j < 3; j++) {
            if (symbols[j]) {
                const symbolData = results[i][j];
                // Clear existing content and classes
                symbols[j].innerHTML = '';
                symbols[j].className = 'symbol';

                // Apply symbol-specific CSS class based on name
                const symbolClass = `symbol-${symbolData.name.replace('-high', '').replace('-medium', '').replace('-low', '').replace('-wild', '').replace('-scatter', '').replace('-bonus', '').replace('-special', '')}`;
                symbols[j].classList.add(symbolClass);
                symbols[j].setAttribute('data-symbol', symbolData.name);
            }
        }
    }
}

// Calcular ganancia simple
function calculateWin(results) {
    const centerLine = [results[0][1], results[1][1], results[2][1], results[3][1], results[4][1]];

    let consecutiveCount = 1;
    let symbol = centerLine[0];

    for (let i = 1; i < centerLine.length; i++) {
        if (centerLine[i].name === symbol.name || symbol.name === 'yeti-wild') {
            consecutiveCount++;
        } else {
            break;
        }
    }

    let multiplier = 0;
    if (consecutiveCount >= 5) multiplier = 50;
    else if (consecutiveCount >= 4) multiplier = 20;
    else if (consecutiveCount >= 3) multiplier = 5;

    if (symbol.name === 'yeti-wild') multiplier *= 2;
    if (symbol.name === 'rocket-high') multiplier *= 1.5;

    return Math.floor(gameState.currentBet * multiplier);
}

// Resaltar s√≠mbolos ganadores
function highlightWinningSymbols() {
    const reels = document.querySelectorAll('.reel');
    reels.forEach(function(reel, index) {
        const centerSymbol = reel.children[1];
        if (centerSymbol) {
