server {
    listen 443 ssl http2;
    server_name moonyetis.io;

    ssl_certificate /etc/letsencrypt/live/moonyetis.io/fullchain.pem;
    ssl_certificate_key /etc/letsencrypt/live/moonyetis.io/privkey.pem;

    root /var/www/html;
    index index.html;

    location / {
        try_files $uri $uri/ /index.html;
        add_header Cache-Control "no-cache, no-store, must-revalidate";
    }
}

